content:
[[module css]]

*:not(:has(.myclass), .myclass *) {display: none !important;}

div#container-wrap {
    background: none !important;
}

html, body,
#container-wrap,
#content-wrap,
#main-content,
#page-content {
    overflow: hidden !important;
    height: 100% !important;
    margin: 0 !important;
    padding: 0 !important;
}

/* 让内容填满整个页面，不允许撑高 */
.myclass {
    height: 100vh !important;
    width: 100vw !important;
    overflow: hidden !important;
    position: fixed;
    top: 0;
    left: 0;
    display: flex;
    align-items: center;
    justify-content: center;
}

html, body {
    overflow: hidden !important;
}

.myclass, .myclass * {display: block !important;}
.myclass {
  position: fixed;
  left: 0;
  right: 0;
  width: 100%;
  height: 100%;
}


.myclass a {
    white-space: nowrap !important;
}


[[/module]]

[[div class="myclass"]]

[[html]]
<div style="text-align:center; padding-top: 0; margin-top: 0;">
    <h2 style="margin-top: 0;">Wikidot is temporarily unavailable (503)</h2>
    <p>Sorry, for some reason our servers cannot display the page you are requesting.</p>
    <p>If the problem persists, check our Twitter page <a href="https://twitter.com/wikidot" target="_blank">@wikidot</a> for updates.</p>
</div>
[[/html]]

[[/div]]
