content:
[[div class="iconContainer blockquote {$name} lvl5-{$iflvl5} bdge-{$ifbdge}"]]
[[div class="iconLeft"]]
[[div class="iconInter"]]
[[/div]]
[[div class="iconContent"]]
{$note}
[[/div]]
[[/div]]

[[div class="contentRight"]]
[[div class="contentInter"]]
[[div class="contentTitle"]]
+ {$name}
++ {$group}组·词条定级：[[span style="display: inline-block; transform: scale(1.5, 1.5); -webkit-transform: scale(1.5, 1.5);"]]{$level}[[/span]]
[[/div]]

[[div class="orides"]]
**词条描述：**{$description}
[[/div]]

[[div class="contentAchievement"]]
**成就描述：**{$description}

**奖励：**{$note}
[[/div]]
[[/div]]
[[/div]]
[[/div]]

[[module CSS]]
div.containerContainer > div.iconContainer.blockquote {
    z-index: 1;
}

div.containerContainer > div.iconContainer.blockquote:hover {
    z-index: 4;
}

div.iconContainer {
    position: relative;
}

div.iconContainer.blockquote {
    border: none;
    background-color: transparent;
    padding: 0;
    position: relative;
    z-index: inherit;
}

div.iconContainer div.iconLeft {
    position: relative;
    width: 150px;
    height: 150px;
    border-radius: 50%;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
    background-color: rgb(var(--light-gray-monochrome, 130, 160, 135));
    background-image: url("http://scpsandboxcn.wikidot.com/local--files/odeos/wl_border_silver.png");
    background-position: center center;
    background-size: 100% 100%;
    background-repeat: no-repeat;
    z-index: inherit;
}

div.iconContainer.lvl5-yes div.iconLeft {
    background-color: rgb(247, 213, 83);
    background-image: url("http://scpsandboxcn.wikidot.com/local--files/odeos/wl_border.png");
}

div.iconContainer.{$name} div.iconInter {
    position: absolute;
    width: 83px;
    height: 83px;
    left: 33.5px;
    top: 33.5px;
    border-radius: 50%;
    background-color: rgb(252, 252, 252);
    background-image: {$icon};
    background-position: center center;
    background-size: 104px 104px;
    background-repeat: no-repeat;
    transition: ease 0.25s;
    -webkit-transition: ease 0.25s;
}

div.iconContent {
    display: none;
    position: absolute;
    width: 170px;
    max-width: unset !important;
    left: -10px;
    top: 130px;
    padding: 1rem;
    text-align: center;
    border-radius: 1.5rem;
    background-color: rgb(252, 252, 252);
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
    filter: drop-shadow(0 0 5px rgba(0, 0, 0, 0.2));
    -webkit-filter: drop-shadow(0 0 5px rgba(0, 0, 0, 0.2));
    animation: appear 0.25s ease forwards;
    -webkit-animation: appear 0.25s ease forwards;
    z-index: 2;
}

div.iconContent::before {
    content: "";
    position: absolute;
    left: 75px;
    top: -10px;
    width: 20px;
    height: 10px;
    border-left: solid 10px transparent;
    border-right: solid 10px transparent;
    border-bottom: solid 10px rgb(252, 252, 252);
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
}

div.iconContent p {
    margin: 0;
}

div.iconContainer div.iconInter:hover {
    transform: scale(1.1, 1.1);
    -webkit-transform: scale(1.1, 1.1);
}

div.iconContainer div.iconInter:hover + div.iconContent {
    display: block;
}

@keyframes appear {
0 {
    top: 130px;
}

100% {
    top: 120px;
}
}

div.contentRight {
    display: flex;
    align-items: center;
    margin-top: -140px;
    margin-left: 20px;
    padding: 18px;
    padding-left: 140px;
    height: 130px;
    border-radius: 35px;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
}

div.contentRight div.contentInter {
    width: 100%;
    flex-shrink: 0;
    flex-grow: 1;
}

div.contentRight * {
    margin: 0;
}

div.contentTitle {
    display: flex;
    align-items: center;
    max-width: 400px !important;
    justify-content: space-between;
    flex-wrap: wrap;
}

div.contentTitle h1,
div.contentTitle h2 {
    padding: 0;
    line-height: 1.5rem;
    margin: 0 0.5rem;
}

div.contentTitle h1 {
    font-size: 1.2rem;
}

div.contentTitle h2 {
    font-size: 1rem;
}

div.contentRight div.contentInter > div.orides p,
div.contentRight div.contentInter > div.contentAchievement p {
    margin: 0.5rem;
}

@media (max-width: 470px) {
div.iconContainer div.iconLeft {
    transform: scale(0.8, 0.8);
    -webkit-transform: scale(0.8, 0.8);
    transform-origin: left top;
    -webkit-transform-origin: left top;
}

div.contentRight {
    margin-top: -145px;
    margin-left: 20px;
    padding: 10px;
    padding-left: 100px;
    height: 110px;
    border-radius: 35px;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
    box-sizing: border-box;
    -webkit-box-sizing: border-box;
}

div.contentRight div.contentInter {
    width: 125%;
    max-width: 125% !important;
    transform: scale(0.8, 0.8);
    -webkit-transform: scale(0.8, 0.8);
    transform-origin: left top;
    -webkit-transform-origin: left top;
}
}

@media (max-width: 360px) {
div.contentRight {
    margin-top: -145px;
    margin-left: 20px;
    padding: 5px;
    padding-left: 100px;
}

div.contentRight div.contentInter {
    width: 133%;
    max-width: 133% !important;
    transform: scale(0.75, 0.75);
    -webkit-transform: scale(0.75, 0.75);
    transform-origin: left top;
    -webkit-transform-origin: left top;
}
}

div.contentAchievement,
div.bdge-yes div.orides,
div.bdge-yes div.iconContent,
div.bdge-yes div.contentTitle h2 {
    display: none !important;
}

div.bdge-yes div.contentAchievement {
    display: block !important;
}
[[/module]]
