[[module css]]
 @import url('https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,800;1,800&display=swap');

:root {
  --header-title: "SITE-001";
  --header-subtitle: "永久同步收容";

  --accent: var(--five-color);
  --head-font: 'Monserrat';
}

a[href*="/scp-2718"]::after {
    content: "2718";
    animation-name: death;
    animation-duration: 2.2s;
    animation-iteration-count: infinite;
    display: inline-block;
    width: 2.25rem;
    overflow-wrap: initial;
}

@keyframes death {
  0%   {content: "1#12";}
  10%  {content: "07!2";}
  20%  {content: "0752";}
  30% {content: "█052";}
  40% {content: "20█8";}
  50% {content: "2018";}
  60% {content: "1018";}
  70% {content: "20█8";}
  80% {content: "1718";}
  90% {content: "1712";}
  100%   {content: "1#12";}
}

.grid-table {
    display: grid;
    grid-template-columns: 1fr 1fr 1.5fr;
    align-items: stretch;
    border: solid 0.5px rgb(var(--accent));
}

.grid-table > * {
    display: flex;
    flex-direction: column;
    border: solid 0.5px rgb(var(--accent));
    background-color: #21252E;
    color: #EDEDED;
    padding: 0.625em;
    justify-content: center;
}

.grid-table > .title {
    color: #21252E;
    background-color: rgb(var(--accent));
    padding: 0;
    font-weight: bold;
    text-align: center;
}

@media screen and (max-width: 620px) {
    .grid-table {
        grid-template-columns: 1fr 1fr;
        border: none;
    }
    .grid-table > .title { display: none; }
    .grid-table > * { justify-content: start; }
    .grid-table > *:nth-child(3n-2) { border-width: 1px 0.5px 0.5px 1px; }
    .grid-table > *:nth-child(3n-1) { border-width: 1px 1px 0.5px 0.5px; }
    .grid-table > *:nth-child(3n) {
        grid-column: span 2;
        margin-bottom: 1.25rem;
        border-width: 0.5px 1px 1px;
     }
    .grid-table > *:nth-child(3n-2)::before, .grid-table > *:nth-child(3n-1)::before {
        content: "SCP";
        display: block;
        color: #21252E;
        background-color: rgb(var(--accent));
        text-align: center;
        font-weight: bold;
        padding: 0.5em;
    }
    .grid-table > *:nth-child(3n-1)::before { content: "001-K"; }
}
[[/module]]

