.bblock:hover {color: #FF6666}

.scp-image-block img {
    border: none;
}

::-webkit-scrollbar {
  width: 18px;
  background: #transparent;
}

::-webkit-scrollbar-track {
  background: #44030F
}

::-webkit-scrollbar-thumb {
  background: #000000;
  border: #84030F 1px solid;
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: #331111;
}

.coolcol .collapsible-block {
    background-color: #44030F;
    border-top: 0.125rem dashed #990100;
    border-bottom: 0.125rem dashed #990100;
}

.raisa_box{
border:solid 1px #252525;
background:#f2f2c2;
padding: 5px;
margin-bottom: 10px;
}

.amd_box{
border:solid 1px #44030F;
background:#990100;
padding: 5px;
margin-bottom: 10px;
}

div.resize img{
width: auto;
height: 150px;
float: left;
margin: 15px;
}

@media (max-width: 767px) {
    div.resize img {
        float: initial;
        margin: auto;
    }
}

.no {
    color: transparent;
}

.no::selection {
    color: #FF6666;
}

#page-title {
display: none;
}

/* 感谢Etinjat的代码协助！*/

.sel em {
    color: transparent;
    font-style:normal;
}

.sel em::selection {
    color: #FF6666;
}

.sel span[style*="text-decoration: underline;"]{
       text-decoration:none !important;
}

.sel span[style*="text-decoration: underline;"]::selection {
    color: #FF6666;
    background: #3c0000 !important;
}

.sel-before {
    z-index: 0;
    position: absolute;
}

.sel-before::selection {
    color: transparent;
}

.sel-after-r::selection {
    color: #FF6666;
}

.sel-after-b {
    z-index: 1;
    position: absolute;
    color: transparent;
}

.sel-after-b::selection {
    color: white;
}

.sel-after-g::selection {
    color: green;
}

.sel-blank{
    visibility: hidden;
}
