[[module CSS]]

@import url('https://fonts.googleapis.com/css2?family=Roboto+Mono:wght@300&display=swap');

 :root{
     --fg-dark: hsl(0, 100%, 25%);
     --fg-accent-light: hsl(0, 100%, 66%);
     --fg-accent: hsl(0, 100%, 30%);
     --fg-accent-dark: hsl(0, 100%, 50%);
     --fg-accent-dark-2: hsl(0, 100%, 25%);
     --fg-dark-2: hsl(0, 100%, 25%);
}

div#header {
     background: url("https://scp-wiki.wdfiles.com/local--files/fragment%3Amfw-2/underwatch_logo.png") 0px 40px no-repeat;
     background-size: auto 100px;
}

div#header h1 {
    background: url();
}

div#header h1 a::before {
    content: "UNDERWATCH WIKI";
    color: red;
    font-family: 'Roboto Mono', monospace;
    margin-left:10%;
    text-shadow:0px 0px 2px black;
}

#side-bar div.menu-item img{
     filter: hue-rotate(550deg) invert(100%);
}

[[/module]]

