[[module CSS]]

div.authorlink-wrapper{margin-top: var(--author-top-adjust, .2em) !important;}
:root {
    --header-title: "ERUDITION";
    --basalt-UI-dark-palette: 50, 45, 45;
    --basalt-bright-element-color: 125, 20, 20;
    --backdrop: url(https://scp-wiki.wdfiles.com/local--files/narrativistics-and-you/nausa.gif);
    --admo-color-bright: 50, 45, 45;
}
.flashy {

display:block;

}

.noflashy {

display:none;

}
body {


    background-attachment: fixed;
    background-size: cover;
    background-blend-mode: difference;

}

body:before {
  content: ' ';
  position: fixed;
  display: block;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, rgba(0, 0, 0, 0) 0%, rgb(var(--basalt-primary-color), 1) 25%, rgb(var(--basalt-primary-color), 1) 50%, rgb(var(--basalt-primary-color), 1) 75%, rgba(0, 0, 0, 0) 100%), var(--backdrop);
  z-index: -1;
  color:#EDEDED;
  background-size:100%;
  background-position:center;
}
div.text{
    color: rgba(128,255,128,0.8);
    padding-top: 40%;
    font-family: monospace;
    font-size: 1.2em;
    padding:5px;
    margin: 0 auto;
}
div.specialmodal > p:nth-child(1)::before {

    font-weight: 900;
    font-size: 130%;
    text-align: center;
    margin-bottom: 1rem;
    padding-bottom: 0.3rem;
    border-bottom: dotted 0.13rem darkslategrey;
    max-width: auto;
    margin-left: auto;
    margin-right: auto;
    display: flex;
    flex-direction: column;
    align-content: stretch;
    align-items: center;
  object-fit: fill;
}

div.specialmodal {

    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.5);
    width: 95%;
    min-height: auto;
    max-height: auto;
    margin-left: auto;
    margin-right: auto;
    text-align: center;
    border: solid 2px #5D5D5D;
    word-break: break-word;


}
@-webkit-keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);}
}

@keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(5000%);}
}
div.peachy {
max-width:auto !important;
padding: 10px 10px 10px 10px !important;
background-color: #050404!important;
}

div.text a {
    color: #90EE90;
    background: transparent;
}
div.text a.newpage {
    color: #90EE90;
    background: transparent;
}
div.text a:hover {
    color: #131;
    background-color: #80FF80;
    padding: 1px;
}


li.folded > .progmask{ display: none; }
li.unfolded > .progmask{ display: block; }


[[/module]]


