[[include :scp-wiki-cn:theme:parallel redstone=*]]
[[module CSS]]
:root {
    --basalt-primary-color: 20,20,20;
    --basalt-secondary-color: 28,29,32;
    --basalt-tertiary-color: 38,39,40;
    --basalt-overtone: 254,254,254;
    --basalt-undertone: 90,150,202;

    --basalt-bright-element-color: 97,155,255;
    --basalt-dark-element-color: 87,150,252;
    --basalt-darker-element-color: 120,160,252;
    --basalt-alternate-color: 130,182,132;
    --basalt-sub-text-color: 155,154,162;

    --diagonal-stripes-angle: 75deg;

    --header-border-width: 0rem;
    --header-border-color: var(--basalt-primary-color);

    --rate-module-background-color: Hormress;
}

body {
    background-color: rgb(45,75,135);
    background-image: url("data:image/svg+xml,%3Csvg version='1.1' id='SCP_Logo' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 2215 2080' style='enable-background:new 0 0 2215 2080;' xml:space='preserve'%3E%3Cstyle type='text/css'%3E .st0%7Bfill:%230F3071;opacity:0.4;%7D .st1%7Bopacity:0.2;fill:%230F3071;animation:linear rotatea 20s infinite;transform-origin: 1107.5px 1090px;%7D @keyframes rotatea%7B0%25%7Btransform: rotate%280deg%29;%7D 100%25%7Btransform: rotate%28360deg%29;%7D%7D%0A%3C/style%3E%3Cpath id='Outer_Circle' class='st0' d='M2215,1351.8000488l-125-139.6999512c5-40.5,7.5-81.3000488,7.5-122.0999756 c0-410-249.3000488-761.7999878-604.5999756-912.2000122L1434.5,0h-654l-58.4000244,177.8999939 C366.7999878,328.2000122,117.5,680,117.5,1090c0,40.8000488,2.5,81.5999756,7.5,122.0999756L0,1351.8000488l327,566.4000244 l183.5-38.4000244C676.5,2005.5,883.2999878,2080,1107.5,2080s431-74.5,597-200.1999512l183.5,38.4000244L2215,1351.8000488 L2215,1351.8000488z M1683,1794.4000244C1526.3000488,1922.9000244,1325.9000244,2000,1107.5,2000 s-418.7999878-77.0999756-575.5-205.5999756l-164.2000122,34.4000244l-270-467.6999512l112.3000031-125.4000244 c-7.6000061-47.5-11.6000061-96.0999756-11.6000061-145.8000488c0-389,243.8999786-721.0999756,586.9000244-851.2000122L837.5,80 h540l52.0999756,158.8000031C1772.6999512,369,2016.5,701,2016.5,1090c0,49.5999756-4,98.3000488-11.5999756,145.8000488 l112.2999268,125.4000244l-270,467.7000732L1683,1794.4000244z'/%3E%3Cpath class='st1' d='M1717.921875,832.1137695c-33.3691406-78.8945313-81.1328125-149.7412109-141.9638672-210.5717773 c-60.8300781-60.8310547-131.6777344-108.5947266-210.5712891-141.9638672 C1283.6855469,445.0214844,1196.9199219,427.5,1107.5,427.5c-89.4204102,0-176.1860352,17.5214844-257.8867188,52.078125 c-78.894043,33.3691406-149.7407227,81.1328125-210.5712891,141.9638672 c-60.8310547,60.8305664-108.5947266,131.6772461-141.9638672,210.5717773C462.5214844,913.8144531,445,1000.5795898,445,1090 c0,89.4199219,17.5214844,176.1855469,52.078125,257.8867188c33.3691406,78.8935547,81.1328125,149.7412109,141.9638672,210.5712891 c60.8305664,60.8310547,131.6772461,108.5947266,210.5712891,141.9638672 C931.3139648,1734.9785156,1018.0795898,1752.5,1107.5,1752.5c89.4199219,0,176.1855469-17.5214844,257.8867188-52.078125 c78.8935547-33.3691406,149.7412109-81.1328125,210.5712891-141.9638672 c60.8310547-60.8300781,108.5947266-131.6777344,141.9638672-210.5712891C1752.4785156,1266.1855469,1770,1179.4199219,1770,1090 C1770,1000.5795898,1752.4785156,913.8144531,1717.921875,832.1137695z M1695,1090 c0,45.0341797-5.1003418,88.8986816-14.7412109,131.0527344l-301.6298828-219.1474609l115.1000977-354.2438965 C1617.0045166,755.4360352,1695,913.7780762,1695,1090z M1018.7929688,1320.6357422h177.4140625L1107.5,1593.6474609 L1018.7929688,1320.6357422z M994.4238281,1245.6357422l-69.8842773-215.0830078L1107.5,897.6240234l182.9609375,132.9287109 l-69.8847656,215.0830078H994.4238281z M900.1704102,955.5527344l-88.7070313-273.012207l232.237793,168.730957 L900.1704102,955.5527344z M915.5644531,1245.6357422H628.5029297l232.237793-168.7304688L915.5644531,1245.6357422z M1171.2988281,851.2714844l232.2382813-168.730957l-88.7070313,273.012207L1171.2988281,851.2714844z M1354.2597656,1076.9052734 l232.2373047,168.7304688h-287.0615234L1354.2597656,1076.9052734z M1107.5,502.5 c110.1264648,0,213.267334,30.4665527,301.4528809,83.4008789L1107.5,804.9189453L806.0473633,585.9006348 C894.2327881,532.9665527,997.3734741,502.5,1107.5,502.5z M721.2709961,647.6611328l115.1005859,354.2441406 l-301.6303711,219.1474609C525.1004028,1178.8986816,520,1135.0341797,520,1090 C520,913.7780762,597.9954224,755.4360352,721.2709961,647.6611328z M567.1781006,1320.6357422h372.7554932l115.2001953,354.5499268 C835.6325684,1655.7078857,650.4542847,1514.9902344,567.1781006,1320.6357422z M1159.8657227,1675.1856689l115.2006836-354.5499268 h372.7553711C1564.5455322,1514.9902344,1379.3669434,1655.7080078,1159.8657227,1675.1856689z'/%3E%3Cpath class='st0' d='M1606.5806885,1286.8186035c0,0-119.7818604-17.137207-172.689209,55.5395508 c-17.3778076,23.8710938-12.1138916,57.3100586,11.7572021,74.6877441 c11.8540039,8.6295166,26.0666504,11.6697998,39.5009766,9.626709 c-54.0567627,61.574707-132.4211426,97.9348145-216.6516113,97.9348145 c-88.2655029,0-167.3813477-39.8709717-220.3256836-102.532959c19.2570801,3.4364014,39.0827637,5.2304688,59.3276367,5.2304688 c185.2729492,0,335.4663086-150.1933594,335.4663086-335.4663086c0-63.1425781-17.4575195-122.2021484-47.7906494-172.638916 c30.02771-40.619873,47.7906494-90.8568115,47.7906494-145.2479248c0-81.2232666-39.6003418-153.1860352-100.5355225-197.6724854 c14.6763916,29.8598633,22.9313965,63.4458008,22.9313965,98.9643555c0,56.9550781-21.2052002,108.9499512-56.1369629,148.5484619 c-56.1446533-42.3200684-126.0008545-67.4197998-201.7252197-67.4197998s-145.5805054,25.0997314-201.7252197,67.4197998 c-34.9317017-39.5985107-56.1369629-91.5933838-56.1369629-148.5484619c0-35.5185547,8.2549438-69.1044922,22.9313965-98.9643555 c-60.9351807,44.4864502-100.5355225,116.4492188-100.5355225,197.6724854 c0,54.3911133,17.7630005,104.6280518,47.7906494,145.2479248c-30.3331909,50.4367676-47.7906494,109.4963379-47.7906494,172.638916 c0,145.4943848,92.6315308,269.3378906,222.1296997,315.8173828c12.864624,20.4974365,28.1241455,39.6970215,45.6578979,57.2307129 c61.081543,61.0810547,142.2939453,94.7207031,228.6767578,94.7207031c94.5686035,0,182.5405273-40.8724365,243.1588135-110.0703125 c2.9042969,11.8181152,9.8277588,22.7160645,20.4368896,30.4393311 c23.8712158,17.3778076,57.3100586,12.1138916,74.6878662-11.7572021 C1659.6889648,1395.5424805,1606.5806885,1286.8186035,1606.5806885,1286.8186035z'/%3E%3C/svg%3E");
    background-repeat: no-repeat;
    background-position: center;
    background-size: 20rem auto;
    background-attachment: fixed;
}

div#header {
    box-shadow: none;
    border-bottom: solid var(--header-border-width) rgb(var(--header-border-color));
}

div#header::before {
    content: "";
    display: block;
    position: absolute;
    left: 0;
    bottom: -0.5rem;
    width: 100%;
    height: 1rem;
    background-color: rgb(var(--basalt-overtone));
    clip-path: polygon(0% 50%, 105% 0%, 95% 100%);
}

div#header h1 a span {
    transform: skew(2deg, 2deg);
}

div#header h1 a span::before,
div#header h1 a span::after {
    text-shadow: 0.1rem 0.1rem rgb(var(--basalt-primary-color));
}

div#page-content {
    margin-top: 3rem;
}

div#page-title {
    display: none;
}

div#page-title,
div.meta-title {
    position: relative;
    max-width: 750px;
    margin-left: auto;
    padding-bottom: 0.5rem;
    border-bottom: none;
}

div#page-title::after,
div.meta-title::after,
hr {
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 0.75rem;
    top: 95%;
    left: 0;
    background-color: rgb(var(--basalt-bright-element-color));
    clip-path: polygon(0% 50%, 100% 0%, 95% 100%);
}

hr {
    position: static;
    margin: 1rem 0;
}

div.meta-title.specialized {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
}

div.meta-title.specialized div {
    padding: 0 0.2rem;
    white-space: nowrap;
}

div.meta-title.specialized p {
    margin: 0;
}

div.meta-title.specialized div.accentA {
    background-color: rgb(var(--basalt-overtone));
    color: rgb(var(--basalt-primary-color));
    clip-path: polygon(0% 0%, 100% 10%, 100% 90%, 10% 100%);
    white-space: nowrap;
}

div.meta-title.specialized div.accentB {
    background-image: url("data:image/svg+xml,%3Csvg version='1.1' id='图层_1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 50 50' style='enable-background:new 0 0 50 50;' xml:space='preserve'%3E%3Cstyle type='text/css'%3E .st0%7Bfill:%23FFFFFF;%7D%0A%3C/style%3E%3Cpolygon class='st0' points='6.1143551,35.807785 35.5547447,44.5060844 49.6058388,8.9829683 11.6496353,3.3260341 '/%3E%3Cpolygon points='0.8223844,41.038929 33.1216545,49.3114357 44.2530403,16.7080288 6.4184914,7.0364962 '/%3E%3C/svg%3E%0A");
    background-size: 100% 100%;
    padding: 0.5rem 0.9rem 0.5rem 0.4rem;
    margin: -0.5rem -0.7rem -0.5rem -0.2rem;
}

div.meta-title.specialized div.accentSub {
    font-size: 1rem;
    line-height: 2.5rem;
    font-weight: 700;
    transform: skew(-5deg, -10deg);
    margin-left: -3rem;
    position: relative;
    top: 1rem;
    text-shadow: 0.1rem 0.1rem 0 rgb(var(--basalt-primary-color));
    z-index: 5;
}

div.meta-title.specialized div.accentSub span.hidden {
    font-size: 0;
    opacity: 0;
}

@media (max-width: 520px) {
    div.meta-title.specialized div.accentSub {
        transform: none;
        top: 0;
        margin-left: 0;
    }

    div.meta-title.specialized div.accentSub span.hidden {
        font-size: inherit;
        opacity: 1;
    }
}

@media (max-width: 440px) {
    div.meta-title.specialized div.accentSub {
        line-height: 3.5rem;
    }
}

div.meta-title + div[style*="text-align: right"] {
    position: relative;
    z-index: 2;
}

div.meta-title + div[style*="text-align: right"] > div.page-rate-widget-box {
    margin-top: -0.8rem;
    perspective: 1.6rem;
}

div.meta-title + div[style*="text-align: right"] > div.page-rate-widget-box span.rate-points {
    position: relative;
}

div.meta-title + div[style*="text-align: right"] > div.page-rate-widget-box span.rate-points::after {
    display: block;
    content: "";
    position: absolute;
    width: 100%;
    height: 100%;
    left: 0;
    top: 0;
    z-index: -1;
    background-color: rgb(var(--basalt-darker-element-color));
    clip-path: polygon(0% 5%, 100% 0%, 95% 100%, 5% 95%);
}

div.meta-title + div[style*="text-align: right"] > div.page-rate-widget-box span.rate-points span.number {
    font-size: 2.5rem;
    line-height: 0.75rem;
    color: rgb(var(--basalt-primary-color));
    text-shadow: 0.1rem 0.1rem 0 rgb(var(--basalt-overtone));
    transform: skew(-5deg, -10deg);
}

div.meta-title + div[style*="text-align: right"] > div.page-rate-widget-box span:is(.rateup, .ratedown, .cancel) {
    position: ralative;
    margin-right: 0.15rem;
    background-color: rgb(var(--basalt-darker-element-color));
    transform: rotateY(-12deg) skew(-15deg, -5deg);
}

div.meta-title + div[style*="text-align: right"] > div.page-rate-widget-box span.rateup {
    z-index: 3;
    margin-left: 0.75rem;
}

div.meta-title + div[style*="text-align: right"] > div.page-rate-widget-box span.ratedown {
    z-index: 2;
}

div.meta-title + div[style*="text-align: right"] > div.page-rate-widget-box span.cancel {
    margin-right: 1.75rem;
}

div#page-content {
    perspective: 3000rem;
}

/* 对话部分 */

div.text {
    position: relative;
    margin: 3rem 0;
}

div.text::before,
div.table div.row::before {
    content: "";
    display: block;
    width: 8rem;
    height: 8rem;
    background-image: var(--icon);
    background-size: cover;
    background-color: rgb(var(--color), 254, 254, 254);
    box-shadow: 0.3rem 0.3rem 0 rgb(var(--basalt-primary-color));
}

div.text:nth-child(3n)::before,
div.table div.row:nth-child(3n)::before {
    transform: rotate(3deg);
}

div.text:nth-child(3n+1)::before,
div.table div.row:nth-child(3n+1)::before {
    transform: rotate(-7deg);
}

div.text:nth-child(3n+2)::before,
div.table div.row:nth-child(3n+2)::before {
    transform: rotate(-9deg);
}

div.text > p:nth-child(1) {
    display: contents;
}

div.text > p:nth-child(1) > tt {
    display: block;
    position: absolute;
    left: 5rem;
    top: 2rem;
    width: 8rem;
    height: 2rem;
    font-size: 1.1rem;
    line-height: 2rem;
    font-family: "Noto Sans SC", sans-serif;
    font-weight: 700;
    text-align: center;
    color: rgb(var(--basalt-primary-color));
    padding: 1rem 2rem 2rem 5rem;
    background-image: url("data:image/svg+xml,%3Csvg version='1.1' id='图层_1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 120 40' style='enable-background:new 0 0 120 40;' xml:space='preserve'%3E%3Cstyle type='text/css'%3E .st0%7Bfill:%23FFFFFF;%7D%0A%3C/style%3E%3Cpolygon points='112.0802917,0 29.7299271,3.2335765 29.8613148,15.0583944 21.6584435,11.7772465 21.9489059,18.3941612 0,14.6642332 33.9051094,40 29.8321171,26.6204376 120,33.8977547 '/%3E%3Cpolygon class='st0' points='2.5328467,16.1094894 24.6475468,21.1875629 23.6569347,15.0583944 30.9027996,18.3029194 32.6204376,4.3868613 111.6861343,4.3868613 113.6861343,30.2992706 27.9927006,25.3065701 29.9635029,33.7153282 '/%3E%3C/svg%3E");
    background-size: 100% 100%;
    transform: scale(0.8) rotate(-10deg);
    z-index: 2;
}

div.text > p:nth-child(1) > tt em {
    font-style: normal;
    color: rgb(var(--basalt-overtone));
    background-color: rgb(var(--basalt-primary-color));
    clip-path: polygon(0% 15%, 100% 15%, 100% 100%, 0% 100%);
}

div.text > p:not(:nth-child(1)) {
    display: block;
    position: relative;
    margin: 0.5rem 0 0 8rem;
    padding: 2rem;
    width: auto;
    max-width: 20rem;
    background-color: rgb(var(--basalt-primary-color));
    z-index: 1;
}

div.text > p:nth-child(2) {
    margin-top: -3rem;
}

div.text:nth-child(3n) > p:nth-child(2) {
    clip-path: polygon(0% 1rem, calc(100% - 1rem) 0%, 100% 100%, 0.5rem calc(100% - 1rem));
}

div.text:nth-child(3n+1) > p:nth-child(2) {
    clip-path: polygon(0% 0.5rem, calc(100% - 0.5rem) 0%, calc(100% - 1rem) 100%, 0.5rem calc(100% - 1rem));
}

div.text:nth-child(3n+2) > p:nth-child(2) {
    clip-path: polygon(0% 0.5rem, calc(100% - 1rem) 0%, 100% calc(100% - 1rem), 0.5rem 100%);
}

div.text.long > p:nth-child(3n) {
    clip-path: polygon(0% 1rem, calc(100% - 1rem) 0%, 100% 100%, 0.5rem calc(100% - 1rem));
}

div.text.long > p:nth-child(3n+1) {
    clip-path: polygon(0% 0.5rem, calc(100% - 0.5rem) 0%, calc(100% - 1rem) 100%, 0.5rem calc(100% - 1rem));
}

div.text.long > p:nth-child(3n+2) {
    clip-path: polygon(0% 0.5rem, calc(100% - 1rem) 0%, 100% calc(100% - 1rem), 0.5rem 100%);
}

div.text.right::before {
    margin-left: auto;
}

div.text.right::after {
    content: "";
}

div.text.right > p:nth-child(1) > tt {
    padding: 1rem 4.7rem 2rem 2.3rem;
    background-image: url("data:image/svg+xml,%3Csvg version='1.1' id='图层_1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' viewBox='0 0 120 40' style='enable-background:new 0 0 120 40;' xml:space='preserve'%3E%3Cstyle type='text/css'%3E .st0%7Bfill:%23FFFFFF;%7D%0A%3C/style%3E%3Cpolygon points='0,0 90.2700729,3.2335765 90.1386871,15.0583944 97.3649597,12.1678829 94.7893448,21.8775063 120,14.6642332 89.3503647,40 82.6496353,26.8832111 11.4379559,32.9270058 '/%3E%3Cpolygon class='st0' points='117.4671555,16.1094894 93.2919693,23.9927006 93.5547409,16.372263 88.9562073,17.1605835 87.3795624,4.3868613 6.3138685,4.3868613 12.6204376,30.6934299 83.7007294,25.7007294 90.9270096,35.4233589 '/%3E%3C/svg%3E%0A");
    right: 5rem;
    left: unset;
    transform: scale(0.8) rotate(5deg);
}

div.text.right > p:not(:nth-child(1)) {
    margin: 0.5rem 7.5rem 0 auto;
}

div.text.right > p:nth-child(2) {
    margin-top: -3rem;
}

div.text.right:nth-child(3n) > p:nth-child(2) {
    clip-path: polygon(100% 1rem, 1rem 0%, 0% 100%, calc(100% - 0.5rem) calc(100% - 1rem));
}

div.text.right:nth-child(3n+1) > p:nth-child(2) {
    clip-path: polygon(100% 0.5rem, 0.5rem 0%, 1rem 100%, calc(100% - 0.5rem) calc(100% - 1rem));
}

div.text.right:nth-child(3n+2) > p:nth-child(2) {
    clip-path: polygon(100% 0.5rem, 1rem 0%, 0% calc(100% - 1rem), calc(100% - 0.5rem) 100%);
}

div.text.right.long > p:nth-child(3n) {
    clip-path: polygon(100% 1rem, 1rem 0%, 0% 100%, calc(100% - 0.5rem) calc(100% - 1rem));
}

div.text.right.long > p:nth-child(3n+1) {
    clip-path: polygon(100% 0.5rem, 0.5rem 0%, 1rem 100%, calc(100% - 0.5rem) calc(100% - 1rem));
}

div.text.right.long > p:nth-child(3n+2) {
    clip-path: polygon(100% 0.5rem, 1rem 0%, 0% calc(100% - 1rem), calc(100% - 0.5rem) 100%);
}

div.text.tech > p:not(:nth-child(1)) {
    background-color: rgba(80, 245, 231, 0.4);
}

/* 移动版可访问性 */

@media (max-width: 575px) {
div.text::before,
div.table div.row::before {
    width: 6rem;
    height: 6rem;
}

div.text > p:nth-child(1) > tt {
    left: 3rem;
    top: 1.5rem;
}

div.text.right > p:nth-child(1) > tt {
    right: 3rem;
    left: unset;
}

div.text > p:not(:nth-child(1)) {
    margin: 0.5rem 0 0 6rem;
}

div.text > p:nth-child(2) {
    margin-top: -2rem;
}

div.text.right > p:not(:nth-child(1)) {
