[[include :scp-wiki:theme:scpedia]]

[[module css]]

body {
    font-size: 13px;
}

.capt {
    margin: 0.2em;
    margin-bottom: -0.5rem;
}

.lazy {
   font-size: xx-small;
   opacity: 0;
   line-height: 1.2em;
   padding: -5em;
}

.lazyagain {
   font-size: 0.95em;
}

.edit {
   font-size: 0.7em;
   font-weight: 200;
}

.wikibox img {
   padding: 0em 0.2em;
   box-sizing: border-box;
   vertical-align: text-bottom;
}

table.infobox {
    border-collapse: collapse;
}

table.infobox th {
    font-size: 1.1em;
    text-align: left;
    font-weight: bold;
    padding:  0.9em 1em 0 1em;
}

table.infobox td {
    font-size: 1.15em;
    text-align: left;
    padding: 1em 0 0 0.85em;
}

.scp-image-block .scp-image-caption > p {
   font-weight: normal;
}

.bigbody {
    font-size: 1.15em;
}

div#extra-div-2::before {
   content: var(--header-title, "SCPedia");
   font-size: 4.45em;
}

@media (max-width: 767px) {
   #header h1 a::before {
        content: var(--header-title, "SCPEDIA");
    }
}

div#extra-div-1::after {
   left: 18.30rem;
}

#toc .title {
   font-size: 0em;
}

#toc .title::before {
   content: "Contents";
   font-size: 0.9rem;
   font-weight: bold;

   margin: 0.2em 0;
   padding: 0 2em;
   text-align: center;
}

#page-content h1 { display: flex; align-items: center; }
#page-content h1 span { display: contents;}

#page-content h1::before {
   content: "     [";
   order: 2;
   font-size: 65%;
   font-weight: 100;
   margin-left: 0.9rem;
}

#page-content h1 span::after {
   content: "edit";
   order: 3;
   color: #0645AD;
   font-size: 65%;
   font-weight: 100;
}

#page-content h1::after {
   content: "]";
   order: 4;
   font-size: 65%;
   font-weight: 100;
}

#page-content .wiki-content-table tr th {
    border: solid 1px #888;
    background-color: #b7b9f7;
}

table {
   border-collapse: collapse;
}

.episode {
   width: 100%;
   font-size: 1.15em;
}

.tab-hr{
   border: solid 1px #888;
   background-color: #b7b9f7;
   padding: 0.3em 0.7em;
}

.tab-mid{
   border: solid 1px #A2A9B1;
   background-color: #F8F9FA;
   padding: 0.3em 0.7em;
   text-align: center;
   line-height: 175%;
}

.tab-mid-dk{
   border: solid 1px #A2A9B1;
   background-color: #C4C8CC;
   padding: 0.3em 0.7em;
   text-align: center;
   line-height: 175%;
}

.tab-long{
   border: solid 1px #A2A9B1;
   background-color: #F8F9FA;
   padding: 0.3em 0.7em;
   text-align: left;
   line-height: 150%;
}

.text-fade {
   overflow: auto;
   display: table;
   -webkit-mask-image: linear-gradient(to bottom, rgb(0,0,0,1), rgba(0,0,0,0) 70%);
}


