[[module css]]
@import url(http://scp-equestria.wikidot.com/component:metro-design-theme/code/1);
[[/module]]

[[module css]]
/* HEADER */
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* FOR MENU */
    background: url(http://scp-equestria.wdfiles.com/local--files/about-the-scp-foundation/SCP-Logo-CN-and-EQ.png) 10px 40px no-repeat;
}

#header h1 a {
    display: block;
    content: "SCP基金会";
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Microsoft JhengHei', Arial, sans-serif;
    font-size: 150%;
    text-decoration: none;
    text-shadow: none;
}

#header h1 a::before {
    content: "SCP基金会";
    color: #ffffff;
}

#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
}

#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}
#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "中国分部 | 小马利亚分部";
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #ffffff;
}

/* For FireFox */
@-moz-document url-prefix() {
.emph {
    /* For the environments which comply with CSS3. */
    font-family: monospace;
    font-style: normal;
    font-weight: normal;
    background-image: url(http://www.scp-wiki.net/local--files/component%3Atheme/dot.png), none;
    background-repeat: repeat-x;
    padding: 0.5em 0 0;
    background-color:transparent;
    background-clip: padding-box, content-box;
    background-size: 1em 1.3em, auto;
}
}

/* For IE10 */
@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
.emph {
    /* For the environments which comply with CSS3. */
    font-family: monospace;
    font-style: normal;
    font-weight: normal;
    background-image: url(http://www.scp-wiki.net/local--files/component%3Atheme/dot.png), none;
    background-repeat: repeat-x;
    padding: 0.5em 0 0;
    background-color:transparent;
    background-clip: padding-box, content-box;
    background-size: 1em 1.3em, auto;
}
}

/* viewport */

@viewport {
    width: device-width;
    zoom: 1.0;
}

/* IE viewport */
@-ms-viewport {
    width: device-width;
    zoom: 1.0;
}

/* opera viewport */
@-o-viewport {
    width: device-width;
    zoom: 1.0;
}

/* chrome viewport - maybe it isn't work... */
@-webkit-viewport {
    width: device-width;
    zoom: 1.0;
}

/* firefox viewport - maybe it isn't work too... */
@-moz-viewport {
    width: device-width;
    zoom: 1.0;
}

/* webkit scrollbar */
::-webkit-scrollbar
{
    width: 9px;    /* for vertical scrollbars */
    height: 9px; /* for horizontal scrollbars */
    border: solid 1px rgba(0, 0, 0, 0.1);
    border-round: 0.5px;
}

::-webkit-scrollbar-track
{
    background: rgba(0, 0, 0, 0.1);
}

::-webkit-scrollbar-thumb
{
    background: rgba(50, 50, 50, 0.3);
}

.page-source {
word-break: break-all;
}

/* Responsive Web Design */
img, embed, video, object, iframe, table {
    max-width: 100%;
}

#page-content div, #page-content div table {
    max-width: 100%;
}

#edit-page-comments {
    width: 100%;
}

/* basic Query for mobile devices */

@media (max-width: 767px) {
.owindow {
    min-width: 80%;
    max-width: 99%;
}

.modal-body .table, .modal-body .table ~ div {
    float: left;
}

.owindow .button-bar {
    float: right;
}

.owindow div a.btn-primary {
    width: 100%;
    float: left;
}

span, a {
word-break: break-all;
}
}

/* Mobile Media Query */
@media (max-width: 479px) {
#search-top-box-input {
    display: none;
}

#search-top-box-form input[type=submit] {
    display: none
}

#page-content {
    font-size: 0.9em;
}

#main-content {
    margin: 0;
}

#recent-posts-category {
width: 100%;
}

#header, .mobile-top-bar {
    max-width: 90%;
}

#side-bar {
    width: 80%;
    position: relative;
}

.top-bar {
    display:none;
}

.mobile-top-bar {
    display: block;
    padding: 0;
}

.page-options-bottom a {
    padding: 0 4px;
}

#header h1 a {
    font-size: 100%;
}

blockquote {
    margin: 1em 0;
}

.license-area {
    font-size: 0.8em;
}

#header {
    background-position: 0 5.5em;
    background-size: 55px 55px;
}

#header h1, #header h2 {
    margin-left: 66px;
}

table.form td, table.form th {
    float: left;
}

/*
td.title {
    width: 30%;
}
*/

td.name {
    width: 15em;
}

table.form td, table.form th {
    padding: 0;
}

#edit-page-title {
    max-width: 90%;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}

#page-content div.title {
word-break: keep-all;
}

}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) {
/*
#search-top-box-input {
    width: 7em;
}
*/

#search-top-box-input {
    display: none;
}

#search-top-box-form input[type=submit] {
    display: none
}

#main-content {
    margin: 0 2em 0 2em;
}

#header, .mobile-top-bar {
    max-width: 90%;
}

#side-bar {
    width: 80%;
    position: relative;
}

.top-bar {
    display:none;
}

.mobile-top-bar {
    display: block;
}

.page-options-bottom a {
    padding: 0 5px;
}

#header h1 a {
    font-size: 120%;
}

blockquote {
    margin: 0.5em;
}

.license-area {
    font-size: 0.85em;
}

#header {
    background-position: 0.5em 4.5em;
    background-size: 66px 66px;
}

#header h1, #header h2 {
    margin-left: 80px;
}

/*
td.title {
    width: 30%;
}
*/

#page-content div.title {
word-break: keep-all;
}

td.name {
    width: 15em;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}
}

/* Mini Tablet Media Query */
@media (min-width: 581px) and (max-width: 767px) {
/*
#search-top-box-input {
    width: 8em;
}
*/

#search-top-box-input {
    display: none;
}

#search-top-box-form input[type=submit] {
    display: none
}

#side-bar {
    width: 80%;
    position: relative;
}

#main-content {
    margin: 0 3em 0 2em;
}

#header, .mobile-top-bar {
    max-width: 90%;
}

.top-bar {
    display: none;
}

.mobile-top-bar {
    display: block;
}

.page-options-bottom a {
    padding: 0 6px;
}

#header h1 a {
    font-size: 140%;
}

.license-area {
    font-size: 0.9em;
}

#header {
    background-position: 1em 4em;
    background-size: 77px 77px;
}

#header h1, #header h2 {
    margin-left: 93px;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 1240px) {
#main-content {
    margin: 0 4em 0 20em;
}

#header, #top-bar #side-bar {
    max-width: 100%;
}

.top-bar li {
    margin: 0;
}

#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
    width: 110px;
}

.page-options-bottom a {
    padding: 0 7px;
}

#header h1 a {
    font-size: 160%;
}

.license-area {
    font-size: 0.95em;
}

#header {
    background-position: 1em 4em;
    background-size: 88px 88px;
}

#header h1, #header h2 {
    margin-left: 106px;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}
}

