[[module css]]
@charset "utf-8";

@import url(/component:theme/code/1);

/*
    SCP Sigma 9
    [2014 Wikidot Theme]
    Created for the SCP Foundation by Aelanna
    Edited for SCP基金会 by MScarlet and Devanos
    re-edited for PornThing by Marleak
*/

#content-wrap {
    position: relative;
    margin: 2em auto 0;
    max-width: 1040px;
    min-height:1300px;
    height: auto !important;
    height: 1500px;
}

div#container-wrap {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/porn-theme/all.png) top left repeat-x;
}

#header {
    width:100%;
    max-width:auto ;
    margin: 0 auto;
}

#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    background-image:url(http://scpsandboxcn.wdfiles.com/local--files/porn-theme/logo.jpg);
    background-repeat:no-repeat;
    background-position:20px 10px; 
}

#header h1 {
    margin-left: 180px;
    padding: 0;
    float: left;
    max-height: 95px;
}
#header h2 {
    margin-left: 190px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Franklin Gothic Medium', Arial, sans-serif;
    font-size: 180%;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0px;
}

#header h1 a::before {
    content: "やらしいざいだん";
    color: #FAD800;
    text-shadow: 3px 3px 5px #000000;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '　高画質　無修正　無料　';
    color: #FFFFFF;
    font-style: normal;
    text-shadow: 1px 1px 1px #000000;
}

#login-status, #login-status a{
    color: #FFFFFF;
}




/* Mobile Media Query */
@media (max-width: 479px) { 
#header {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/porn-theme/logo55.jpg) 0 5.5em no-repeat;
    background-size: 55px 55px;
}
#header h1, #header h2 {
    margin-left: 66px;
}
}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) { 
#header {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/porn-theme/logo66.jpg) 0.5em 4.5em no-repeat;
background-size: 66px 66px;
}
#header h1, #header h2 {
    margin-left: 80px;
}
}

/* Mini Tablet Media Query */
@media (min-width: 581px) and (max-width: 767px) { 
#header {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/porn-theme/logo75.jpg) 1em 4em no-repeat;
background-size: 77px 77px;
}
#header h1, #header h2 {
    margin-left: 93px;
}
}

/* MAIN BODY & CONTENT
------------------------------*/

body {
    background:#FFFFFF url(http://scpsandboxcn.wikidot.com/local--files/porn-theme/all.png ) repeat scroll center top;
    width:100%;
    color:#000000;
    padding:0;
}

#main-content {
    background-image:none;
    background-repeat: no-repeat;
    border-radius: 10px 10px;
    -moz-border-radius: 10px 10px;
    -webkit-border-radius: 10px 10px;
    background-color:#000000;
    border:4px solid #000000;
    margin:0 2em 0 20em;
    padding-left:2em;
    padding-right:2em;
    padding-top:2em;
}

}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
#header {
background: url(http://scpsandboxcn.wdfiles.com/local--files/porn-theme/logo75.jpg) 1em 4em no-repeat;
background-size: 88px 88px;
}
#header h1, #header h2 {
    margin-left: 106px;
}
}
/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
#main-content {
margin: 0 4em 0 23em;
}

.top-bar {
right: 10%
}

#header, .top-bar, .mobile-top-bar, #side-bar {
max-width: 100%;
}

.top-bar {
display:block;
}

.mobile-top-bar {
display: none;
}

.top-bar li {
margin: 0;
}

.top-bar ul li.sfhover ul li a, .top-bar ul li:hover ul li a {
width: 100px;
}

.page-options-bottom a {
padding: 0 7px;
}

#header h1 a {
font-size: 160%;
}

.license-area {
font-size: 0.95em;
}

#header {
background: url(/local--files/component:theme/logo-t.png) 1em 4em no-repeat;
}

#header h1, #header h2 {
    margin-left: 106px;
}

/* Desktop Media Query */
@media (min-width: 980px) and (max-width: 1199px) {
.top-bar {
display:block;
}

.mobile-top-bar {
display: none;
}
}

/* Wide Monitor Media Query */
@media (min-width: 1200px) {
.top-bar {
display:block;
}

.mobile-top-bar {
display: none;
}

#content-wrap {
max-width: 1500px;
}
}
}

/* Display Black Block by Nanimono Demonai */
.bblock {
    color: #000000;
    background-color:#000000;
    transition: 2s;
    text-decoration: none;
}

.bblock:hover {
    background-color:#000000;
    color: #006600;
    text-decoration: none;
}

.dblock {
    color:#000000;
    background-color:#000000;
    transition: 2s;
    text-decoration: none;
}

.dblock:hover {
    background-color:transparent;
    text-decoration: none;
}

/* off-canvas */

.close-menu {
    display: none;
}

@media (max-width: 767px) {

.page-history tbody tr td:last-child {
width: 35%;
}

.owindow {
    min-width: 80%;
    max-width: 99%;
}

.modal-body .table, .modal-body .table ~ div {
    float: left;
}

.owindow .button-bar {
    float: right;
}

.owindow div .btn-primary {
width: 100%;
float: left;
}

.owindow div .btn-primary ~ div {
width: 100%;
}

.yui-navset {
z-index: 1;
}

#navi-bar, #navi-bar-shadow {
    display: none;
}

.open-menu a {
    position: fixed;
    top: 0.5em;
    left: 0.5em;
    z-index: 15;
    font-family: 'Nanum Gothic', san-serif;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    border: 0.2em solid #888 !important;
    background-color: #fff !important;
    border-radius: 3em;
    color: #888 !important;
}

.open-menu a:hover {
    text-decoration: none !important;
    -webkit-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -moz-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -ms-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -o-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
}

#main-content {
    max-width: 90%;
    margin: 0 5%;
    padding: 0;
    -webkit-transition: 0.5s ease-in-out 0.1s;
    -moz-transition: 0.5s ease-in-out 0.1s;
    -ms-transition: 0.5s ease-in-out 0.1s;
    -o-transition: 0.5s ease-in-out 0.1s;
    transition: 0.5s ease-in-out 0.1s;
}

#side-bar {
    display: block;
    position: fixed;
    top: 0;
    left: -25em;
    width: 17em;
    height: 100%;
    background-color: rgb(184, 134, 134);
    overflow-y: auto;
    z-index: 10;
    padding: 1em 1em 0 1em;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    -moz-transition: left 0.5s ease-in-out 0.1s;
    -ms-transition: left 0.5s ease-in-out 0.1s;
    -o-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
}

#side-bar:after {
    content: "";
    position: absolute;
    top: 0;
    width: 0;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);

}

#side-bar:target {
    display: block;
    left: 0;
    width: 17em;
    margin: 0;
    border: 1px solid #dedede;
    z-index: 10;
}

#side-bar:target + #main-content {
    left: 0;
}

#side-bar:target .close-menu {
    display: block;
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: rgba(0,0,0,0.3) 1px 1px repeat;
    z-index: -1;
}
}
