[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Gloria+Hallelujah&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Caveat:wght@400..700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Oregano:ital@0;1&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Indie+Flower&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Cutive+Mono&display=swap');

:root {
    --header-title: 'reSCiPes';
    --header-subtitle: 'Prepare, Cook, Serve up!';
    --logo-image: url('http://scp-wiki.wikidot.com/local--files/re-scp-es/SCP%20Cooking%20logo.png');
    --header-title-color: 192,192,192;
    --header-subtitle-color: 192,192,192;
    --top-bar-link-color: 192,192,192;
    --header-border-color: 99,107,96;
    --link-color: 179,122,18;
    --link-visited-color: 179,122,18;
    --link-newpage-color: 179,122,18;
    --gradient-header: url('');
    --header-font: Cutive Mono, serif;
}

#footer::before {
    background-image: url('');
    content: "";
}

@media only screen and (max-width: 56.25rem) {
  :root {    --gradient-header: url('');
}
}
   #footer::before {
      font-size: 1.5rem;
  content: "";
   }

#page-title { text-align: center; }

/*PAPERDIV JAY*/
.notejay {
     background-image:
     linear-gradient(90deg, transparent 2.35em, rgb(255, 101, 106) 2.35em, rgb(255, 101, 106) 2.5em, transparent 2.5em),
     linear-gradient(rgba(213, 194, 96,  0.5) .1em, transparent .1625em);
     background-position: 1rem 0.75rem;
     background-size: 100% 1.8rem;
     font-size: 1.1em;
     font-family: "Gloria Hallelujah", serif;
     box-shadow: 5px 3px 5px #C6BB87;
     border-width: 1px;
     border-style: dotted;
     border-color: rgba(198, 187, 135);
     padding-left: 2rem;
}

.notejay pre {
     white-space: pre;
     white-space: normal;
     width: 100%;
     overflow: hidden;
}

.notejay >*,
     --wght: 400;
     padding: 0 0 0 1rem;
     font-weight: 400;
     letter-spacing: 0.05em;
     outline: none;
}

.notejay >* {
     line-height: calc(var(--base-font-size) * 1.62);
}

