/*TOP BUTTON*/
.stocky{
    float: right;
    position: sticky;
    bottom: 20px;
    right: 20px;
    background-color: #FF9FA2;
    padding: 2px;
    border-radius: 15px;
    box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
    margin-right: 10px;
    transition: 0.3s !important;
}
.stocky:hover{
    background-color :#e5d4be;
}
.stocky p{
    line-height: 0;
}


/*RECIPE INSTRUCTIONS BLOCK*/

.recipeneed{
   background-color: transparent;
   padding: 15px;
   text-align: left;
   margin-left: 3rem;
}

.recipeact{
   background-color: transparent;
   padding: 15px;
   text-align: left;
   margin-left: 1.5rem;
}

/*PICTURE BLOCK*/
.recipepic{
   width: 50%;
   text-align: center
   display: block;
   margin-left: auto;
   margin-right: auto;
   font-style: italic;
   padding: 10px;
   transform: rotate(5deg);
}

/*BLOG BLOCK*/

.canon-block {
    --link-color: #b01;
    font-size: 1.05em;
    text-align: center;
    position: relative;
    padding: 1.125rem 1.675rem;
    background-color: #fff;
    box-shadow: 0 0 .325rem rgb(0 0 0 / 0.25);
}
