[[module css]]
@font-face {
    font-family: '함렛Bold';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2110@1.0/Hahmlet-Bold.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
}
@font-face {
    font-family: '웰컴체R';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2110@1.0/OTWelcomeRA.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
}


:root:lang(ko) {
    --header-title: "빨간끈";
    --header-subtitle: "자정 {$years} 분 전";
    --header-font: 'Secular One', '함렛Bold', sans-serif;
    --title-font: 'Secular One', '함렛Bold', sans-serif;
    --body-font: 'Telex', '웰컴체R', sans-serif;
    --mono-font: 'PT Mono', 'Nanum Gothic Coding', monospace;

    --minutes-to-midnight: {$years};
}

textarea {
   font-family: var(--mono-font);
}
input#edit-page-title.text {
   font-family: var(--title-font);
}
[[/module]]
