function retxt(){
document.getElementById("huee").title="但也是新的旅途的开始";
document.getElementById("see1").innerHTML="[ 欢 迎 新 同 伴 ]";
document.getElementById('player').innerHTML='<embed src="http://jsdx.sc.chinaz.com/Files/DownLoad/sound1/201503/5611.wav" loop="true" autostart=true name=bgss width="0" height="0" autoplay="autoplay">';
}

function load(){
document.getElementById("location").innerHTML="欢迎"+ localAddress["province"]  +"用户登陆麦克斯韦宗网络" + "<br>体验另一个真实的世界!" ;
}
</script>

<body onload="load();">

<p id="player" style="font-size:0px"></p>

<div id="block1" style="border:double  5px #BCB7B6;  background-color: #0E5293; font-family: Verdana, Arial, sans-serif; border-radius:30px /70px; background:#0E5293; background:linear-gradient(#0E5293, #0E5293); text-align: center; padding-top: 2px; padding-bottom: 2px; padding-right: 50px; padding-left: 20px;" > 
