[[module CSS]]
/* == COMMON == */
 body {
	text-underline-offset: 25%;
}

/* == CUSTOM DIV BLOCKS == */
 .dankesite {
	margin: 1rem 0;
	padding: 0 1rem;

	color: hsl(60, 60%, 67%);
	background: hsl(60, 100%, 7%);
	border: medium ridge hsl(60, 33%, 50%);

	font: 1.25rem var(--mono-font);
	text-align: center;
}

 .response {
	padding: 0 1rem;

	color: hsl(240, 100%, 27%);
	border: thin solid black;

	font: 1.05rem var(--mono-font);
}

 .intscpfn-1 {
	max-width: 640px;
	margin: auto auto 1.2rem;
	padding: 0 .6rem;

	color: hsl(0, 0%, 95%);
	background: hsl(0, 100%, 20%);
	border: thin solid hsl(0, 100%, 20%);
	border-radius: 1.8rem;
	box-shadow:
		inset 0 10px 20px rgb(255 204 204 / 30%),
		inset 0 -15px 30px rgb(48 0 0 / 50%);

	font: 1.5rem var(--header-font);
	text-align: center;
}

 .intscpfn-2, .intscpfn-3 {
	width: 480px;
	margin: 1.2rem auto 0;
	padding: 0 .6rem;

	border-radius: 1.8rem;
}

 .intscpfn-2 {
	background: hsl(0, 100%, 95%);
	border: thin solid hsla(0, 100%, 20%);
}

 .intscpfn-3 {
	background: hsl(60, 100%, 95%);
	border: thin solid hsl(60, 100%, 20%);
}

 @media (max-width: 479px) {
	 .intscpfn-1, .intscpfn-2, .intscpfn-3 {
		width: unset;
	}
}

/* == Blinking Text Cursor ==
 * Adapted From:
 *	Word Processor Theme by stormbreath
**/
 #u-append-blink-bar p:last-child::after {
	position: relative;
	bottom: .067rem;
	left: .07rem;
	content: "█";

	animation: blink 1.5s infinite;
	color: hsl(240, 100%, 27%);
}

 @keyframes blink {
	 to {
		opacity: 0;
	}
}
[[/module]]
