[[module CSS]]
/* == FORMATTING | [GENERAL] == */
 .meta-title, h3, h4 {
	font-family: "Trebuchet MS", "Trebuchet", "Verdana", "Arial", "Helvetica", sans-serif;
}

 .meta-title {
	font-weight: 700;
}

/* == FORMATTING | [SPECIAL] == */
 table.form {
	width: 100%;
	margin: 2rem auto;
}

 table.form td {
	float: unset;

	text-align: center;
}

 table.form td.type-border {
	display: block;
	margin: .4rem auto;

	background: white;
	border: thin solid hsl(0, 0%, 47%);
	font-size: 1.35rem;
}

 .terminal-span {
	font: 1.05rem var(--mono-font);
	font-weight: bold;
	letter-spacing: .3px;
}

/* == CUSTOM DIV BLOCKS == */
 .report-box {
	margin: 1rem 0;
	padding: 0 1rem;

	background: white;
	border: thick solid hsl(0, 0%, 5%);
}

 .report-box :is(h3, h4) {
	text-align: center;
}

 .report-box hr {
	border-color: hsl(0, 0%, 5%);
}

 .report-box.blue-tint {
	background: hsl(240, 91%, 86%);
	border-color: hsl(240, 62%, 66%);
}

 .report-box.blue-tint hr{
	border-color: hsl(240, 62%, 66%);
}

 div.blockquote.narrow {
	width: fit-content;
	margin: auto;

	background: rgb(var(--techno-secondary) / .2);

	text-align: center;
}
[[/module]]
