[[div style="background-color:#EEE;border:1px solid #000;width:670px;"]]
[[div class="pbgone" style="background-color:#006abf;color:#FFF;font-weight:bold;padding:5px;"]]
[[=]]
[[size 125%]] {{SCiPNET Email Server}} [[/size]]
[[/=]]
[[/div]]
[[div style="padding:5px"]]
[[html]]
<head>
<meta name="viewport" content="width=device-width, initial-scale=1">
<style>
body, html {
    height: 100%;
    font-family: Verdana, Helvetica, sans-serif;
}

* {
    box-sizing: border-box;
}

.bg-img {
    /* The image used */
    background-image: url("https://scp-wiki.wdfiles.com/local--files/the-great-hippo/scp_trans.png");

    min-height: 380px;

    /* Center and scale the image nicely */
    background-position: center;
    background-repeat: no-repeat;
    background-size: auto;
}

/* Add styles to the form container */
.container {
    position: absolute;
    right: 1;
    margin: 20px;
    max-width: 300px;
    padding: 16px;
    background-color: white;
    opacity: 0.5;
}

/* Full-width input fields */
input[type=text], input[type=password] {
    width: 100%;
    padding: 15px;
    margin: 5px 0 22px 0;
    border: none;
    background: #f1f1f1;
}

input[type=text]:focus, input[type=password]:focus {
    background-color: #ddd;
    outline: none;
}

/* Set a style for the submit button */
.btn {
    background-color: #006abf;
    color: white;
    padding: 16px 20px;
    border: none;
    cursor: pointer;
    width: 100%;
    opacity: 0.9;
}

.btn:hover {
    opacity: 1;
}

#splash {
    background-color: #f2f2f2;
    background-image: url(https://domain.com/image.jpg);
    background-position: center;
    background-repeat: no-repeat;
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 9999;
}


</style>
</head>
<body>
<script>
function myFunction() {
    document.getElementById("myForm").submit();
}
</script>
<div class="bg-img">
  <form action="https://scp-wiki.wikidot.com/scp-2641/offset/2" target="_parent" >
    <div class="container">
      <h2>Welcome,<br> Dr. J Fiael</h2>

      <label for="email"><b>Security Clearance Lock</b></label>
      <input type="text" placeholder="Enter Credentials" name="email" onclick="myFunction()" value="Enter Credentials">



      <button type="submit" class="btn">Submit</button>
    </div>
  </form>
</div>

<p><center>_____________________________________________________________</p></center>

</body>
[[/html]]

[[/div]]
[[/div]]
