[[module CSS]]
#page-title {
    display: none;
}

.blbf-main > div::before {
    top: -0.5rem;
    left: -0.35em;
}

.blbf-main > div {
    border: 0.50rem black solid;
    margin-left: auto;
}

.blbf-main > div::before {
    background-color: black;
}

span.hilighter {
    background-color: rgb(var(--black-monochrome));
    color: white;
    font-weight: bold;
    padding: 0.2em;
    font-size: 108%;
    line-height: 1.2;
}

a.collapsible-block-link {
    color: transparent!important;
    background-color: transparent!important;
}

[[/module]]
[[=]]
+ [http://scp-jp.wikidot.com/scp-2744 イマーシブモードにアクセス]
[[/=]]
