[[include :scp-wiki-cn:theme:black-highlighter-theme]]
[[module CSS]]
#page-title {
    display: none;
}

div.expoblock {
    border: 0.5rem solid #1a1a1a;
    padding: 1rem;
}
.expoblock hr {
    height: 0.6rem;
    margin: 1.5em 0;
    background-color: black;
}

