content:
[[module CSS]]

#header, #side-bar, #footer, #license-area,
#action-area-top, #page-title, #breadcrumbs,
.ko-footer, .page-tags, #page-info-break,
#page-options-container, #action-area {
  display: none !important;
}

#container-wrap-wrap, div#container-wrap, #container {
  background: none;
  z-index: 100;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
}

#main-content {
  z-index: 200;
  position: fixed;
  top: 0;
  left: 0;
  margin: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  box-sizing: border-box;
}

#container {
  background-image: url('http://scpko.wikidot.com/local--files/fragment:scp-308-ko-7/sky');
  background-size: cover;
  background-position: bottom;
  background-color: #47190f;
}

.atag, .atag:hover {
  position: fixed;
  top: 50px;
  left: 0;
  width: 100%;
  height: calc( 100% - 100px );
  display: flex;
  text-align: center;
  box-sizing: border-box;
  text-decoration: none;
  align-items: flex-end;
  align-content: flex-end;
  flex-wrap: wrap;
  justify-content: center;
}

@media (max-width: 1024px) and (max-width: 20vh){
  #container {
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
  }
  .atag, .atag span {
    font-size: unset;
  }
}

