[[module CSS]]

blockquote {

    background-color: black;

    border: double 3px #80FF80

}
div.terminal{
        width: @display-width;
        height: @display-height;
    border: 1px solid black;
    border: solid 3px #BBBBBB;
    border-radius: 16px;
    background-color: #131;
/* black shadow at the top of the terminal */
  	background-image: 
    	radial-gradient(ellipse 1000% 100% at 50% 90%, transparent, #121);
  	background-position: center;
  	display: block;
/* shadow around the terminal */
  	box-shadow: inset 0 0 10em 1em rgba(0,0,0,0.5);
/* prevents scanline from forming a scrollwheel */
  	overflow:hidden;
}
div.scanline{
    margin-top: -40%;
    width: @display-width;
    height: 60px;
    position: relative;
    pointer-events: none;
    -webkit-animation: scan 5s linear 0s infinite;; /* Safari 4.0 - 8.0 */
    animation: scan 5s linear 0s infinite;
    background: linear-gradient(to bottom, rgba(56, 112, 82,0), rgba(56, 112, 82,0.1)) !important;
}

div.text{
    color: rgba(128,255,128,0.8);
    padding-left: 2em;
    padding-top: 40%;
font-family: monospace;
font-size: 0.85em;
    }

/* Safari 4.0 - 8.0 */
@-webkit-keyframes scan{
    from{ transform: translateY(-10%);}
    to{  transform: translateY(2000%);}
}


div.text a {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a.newpage {
    color: #90EE90;
    text-decoration: none;
    background: transparent;
}
div.text a:hover {
    color: #131;
    text-decoration: underline;
    background-color: #80FF80;
    padding: 1px;
}
div.text a:hover::before {
content: "> ";
}
span.highlight{
color: #90EE90
}
[[/module]]
[[div class="terminal"]]
[[div class="scanline"]]
[[/div]]
