[[include :scpko:theme:black-highlighter-theme]]
[[include :scpko:theme:the-foundation-theme]]

[[module css]]
:root {
    --rating-module-text-color: var(--swatch-menutxt-light-color);
    --rating-module-button-color:  var(--swatch-menutxt-light-color);
}

#page-content > div:nth-child(1) {
    padding: 0.5rem 0.5rem 0 0.5rem;
}

.page-rate-widget-box {
    margin: 0;
    background:
     linear-gradient(0deg,
     rgba(var(--swatch-primary-darkest),.55) 0,
     rgba(var(--swatch-primary-darkest),.45)) !important;
}
[[/module]]
[[module css]]
.danger-diamond a,
.danger-diamond a:hover {
    color: rgba(0,0,0,0) !important;
    box-shadow: initial !important;
}

iframe.html-block-iframe:nth-child(5) {
    min-height: 9.375rem;
}


warning-container,
.warning-container * {
    -webkit-box-sizing: border-box;
       -moz-box-sizing: border-box;
            box-sizing: border-box;
}

.warning-container {
    width: 90%;
    margin: 0 auto;
    background-color: rgb(var(--dark-gray-monochrome));
    color: rgb(var(--swatch-text-light));
    font-weight: 900;
    border: 0.5em solid rgb(var(--swatch-primary-darkest));
}

.warning-container .warning-top {
    font-size: 200%;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    padding: 1rem;
    font-family: var(--title-font);
    background-color: rgb(var(--swatch-primary));
    background-image: url("https://scp-wiki.wikidot.com/local--files/theme:the-foundation-theme/foundation-circles-bg.png");
    -webkit-background-size: 50% 50%;
    -moz-background-size: 50%;
    -o-background-size: 50%;
    background-size: 50%;
    background-attachment: fixed;
    background-position: 50% 50%;
}

.warning-container .warning-middle {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    text-align: center;
    line-height: 1;
    padding: 1rem;
    font-size: 125%;
}

.warning-container .warning-bottom {
    display: -ms-grid;
    display: grid;
    -ms-grid-columns: 50% 50%;
    grid-template-columns: 50% 50%;
    position: relative;
    margin-top: 0.5em;
}

.warning-container .warning-bottom .header {
    white-space: pre;
    /* makes newline work */
    font-size: 0.750em;
    color: #888888;
    color: rgb(var(--medium-accent));
    text-transform: uppercase;
    height: 2.35em;
    position: relative;
    margin-bottom: -2.5em;
    margin-top: -1em;
    top: 0;
    left: 0;
    background: #aaa;
    background: rgb(var(--swatch-menubg-medium-color));
    color: #fcfcfc;
    color: rgb(var(--swatch-menutxt-light-color));
    padding-left: 1.4em;
    padding-top: 0.35em;
    padding-bottom: 0.7em;
    font-weight: 700;
    border-top: 0.0625rem rgb(var(--swatch-border-color)) solid;
}

.warning-container .warning-bottom .cell {
    padding: 1.95em 1em 0.5em 1em;
    position: relative;
    border-bottom: 0.0625rem rgb(var(--swatch-border-color)) solid;
    font-family: var(--mono-font);
}

.warning-container .warning-bottom .cell p,
.warning-container .warning-bottom .cell iframe {
    margin: 0.25em 0 0 0;
    padding: 0;
}

.warning-container .warning-body {
    font-weight: 600;
    padding: 1rem 2rem;
    margin-bottom: 2.5rem;
    line-height: 1.5;
    font-size: 115%;
    text-align: center;
    font-family: var(--mono-font);
    background-color: rgb(var(--swatch-primary-darkest),0.25);
}

@media (max-width: 500px ) {
    .warning-container .warning-body {
     padding: 0.5rem 1rem;
     margin-bottom: 1.5rem;
     font-size: 95%;
    }
}

