content:
[[include theme:black-highlighter-theme]]
[[include theme:the-foundation-theme]]

[[module css]]
:root {
	--rating-module-text-color: var(--swatch-menutxt-light-color);
	--rating-module-button-color:  var(--swatch-menutxt-light-color);
}

#page-content > div:nth-child(1) {
	padding: 0.5rem 0.5rem 0 0.5rem;
}

.page-rate-widget-box {
	margin: 0;
	background:
	 linear-gradient(0deg,
	 rgba(var(--swatch-primary-darkest),.55) 0,
	 rgba(var(--swatch-primary-darkest),.45)) !important;
}
[[/module]]

[[module css]]
@import url("https://use.typekit.net/put0nby.css");

.warning-container iframe {
	min-height: 9.375rem;
}

.page-title {
	--wght: 900;
	margin-top: 1rem;
	min-height: 4rem;
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	width: -webkit-calc(100% - 1em - 0.5rem);
	width: -moz-calc(100% - 1em - 0.5rem);
	width: calc(100% - 1em - 0.5rem);
	-webkit-box-align: center;
	-webkit-align-items: center;
	-moz-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	-moz-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	border: initial;
	font-size: 3em;
	line-height: 1;
	text-align: center;
	color: rgb(var(--white-monochrome));
	background-color: rgb(var(--swatch-primary));
	padding: 0.5em;
	border: 0.25rem solid rgb(var(--white-monochrome));
	position: relative;
	z-index: 1;
	font-family: var(--title-font);
	font-weight: 900;
}

.page-title::before {
	content: " ";
	pointer-events: none;
	position: absolute;
	width: 100%;
	height: 100%;
	top: 0;
	left: 0;
	background-image: url(https://nu-scptheme.github.io/Black-Highlighter/img/logo.svg);
	background-repeat: no-repeat;
	background-position: center center;
	-webkit-background-size: 100% 100%;
	-moz-background-size: 100%;
	-o-background-size: 100%;
	background-size: 100%;
	background-color: rgb(var(--swatch-primary));
	z-index: -1;
	opacity: 0.25;
}

.warning-container,
.warning-container * {
	-webkit-box-sizing: border-box;
	   -moz-box-sizing: border-box;
			box-sizing: border-box;
}

.warning-container {
	--wght: 900;
	width: 90%;
	margin: 0 auto;
	background-color: rgb(var(--dark-gray-monochrome));
	color: rgb(var(--swatch-text-light));
	font-weight: 900;
	border: 0.5em solid rgb(var(--swatch-primary-darkest));
}

.warning-container .warning-top {
	font-size: 200%;
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-align: center;
	-webkit-align-items: center;
	-moz-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	-moz-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	padding: 1rem;
	font-family: var(--title-font);
	background-color: rgb(var(--swatch-primary));
	background-image: url("https://scp-wiki.wikidot.com/local--files/theme:the-foundation-theme/foundation-circles-bg.png");
	-webkit-background-size: 50% 50%;
	-moz-background-size: 50%;
	-o-background-size: 50%;
	background-size: 50%;
	background-attachment: fixed;
	background-position: 50% 50%;
}

.warning-container .warning-body {
	--wght: 300;
	font-weight: 300;
	padding: 1rem;
	margin: 1rem;
	line-height: 1.5;
	font-size: 115%;
	font-family: var(--mono-font);
	background-color: rgb(var(--swatch-primary-darkest),0.25);
}

.warning-container .warning-body,
.warning-container .warning-body * {
	font-variation-settings: 
		"wght" var(--mono-wght), 
		"slnt" var(--slnt), 
		"CASL" var(--CASL), 
		"CRSV" var(--CRSV), 
		"MONO" var(--MONO);
}

.collapsible-block {
	width: 100%;
}

.collapsible-block-folded > a,
.collapsible-block-unfolded-link > a {
	--wght: 900;
	display: -webkit-inline-box;
	display: -webkit-inline-flex;
	display: -moz-inline-box;
	display: -ms-inline-flexbox;
	display: inline-flex;
	width: 100%;
	height: 100%;
	-webkit-box-align: center;
	-webkit-align-items: center;
	-moz-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	-moz-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	color: rgb(var(--white-monochrome));
	font-weight: 900;
	font-size: 1.25em;
	background: rgb(var(--gray-monochrome));
	-webkit-border-bottom-left-radius: 0.5em;
	-moz-border-radius-bottomleft: 0.5em;
	border-bottom-left-radius: 0.5em;
	-webkit-border-bottom-right-radius: 0.5em;
	-moz-border-radius-bottomright: 0.5em;
	border-bottom-right-radius: 0.5em;
}

table.wiki-content-table {
	width: 100%;
	font-size: 1.5em;
	text-align: center;
	margin: 0 auto;
}

table.wiki-content-table,
table.wiki-content-table * {
	border-color: rgb(var(--black-monochrome)) !important;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}

table.wiki-content-table tr:last-of-type {
	--wght: 700;
	width: 100%;
	display: -webkit-inline-box;
	display: -webkit-inline-flex;
	display: -moz-inline-box;
	display: -ms-inline-flexbox;
	display: inline-flex;
	-webkit-box-align: stretch;
	-webkit-align-items: stretch;
	-moz-box-align: stretch;
	-ms-flex-align: stretch;
	align-items: stretch;
	margin: 0;
	font-weight: 700;
}

table.wiki-content-table td {
	display: -webkit-inline-box;
	display: -webkit-inline-flex;
	display: -moz-inline-box;
	display: -ms-inline-flexbox;
	display: inline-flex;
	-webkit-flex-wrap: wrap;
	-ms-flex-wrap: wrap;
	flex-wrap: wrap;
	-webkit-box-align: center;
	-webkit-align-items: center;
	-moz-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	-moz-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	margin: 0;
	-webkit-box-flex: 2;
	-webkit-flex-grow: 2;
	-moz-box-flex: 2;
	-ms-flex-positive: 2;
	flex-grow: 2;
	-webkit-flex-basis: 50%;
	-ms-flex-preferred-size: 50%;
	flex-basis: 50%;
	background-color: rgb(var(--swatch-primary-darkest));
	color: rgb(var(--white-monochrome));
	text-transform: uppercase;
	line-height: 1;
	text-align: center;
}

table.wiki-content-table td * {
	width: 100%;
}

table.wiki-content-table td span:nth-of-type(even) {
	font-family: var(--mono-font);
	font-size: 0.75em;
}

table.wiki-content-table td span:nth-of-type(even),
table.wiki-content-table td span:nth-of-type(even) * {
	font-variation-settings: 
		"wght" var(--mono-wght), 
		"slnt" var(--slnt), 
		"CASL" var(--CASL), 
		"CRSV" var(--CRSV), 
		"MONO" var(--MONO);
}

/* Doc 3 GoI Entry */

div.database_container,
div.database_container * {
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}


div.database_container {
	width: 100%;
	background-color: rgb(var(--swatch-primary-darkest));
	color: rgb(var(--white-monochrome));
	position: relative;
}

div.database_main {
	background-color: rgb(var(--black-monochrome), 0.5);
	margin: 1rem;
	padding: 1rem;
	display: -ms-grid;
	display: grid;
		grid-template-areas:
		"title"
		"top"
		"main";
	-ms-grid-columns: 100%;
	grid-template-columns: 100%;
	position: relative;
	left: 0;
	overflow-wrap: break-word;
	word-wrap: break-word;
	-webkit-hyphens: auto;
	-ms-hyphens: auto;
	-moz-hyphens: auto;
	hyphens: auto;
}

div.database_main .cell[data-label] {
	padding: 2.55em 0.5em 0.5em 0.5em;
	border: 0.0625rem #CCCCCC solid;
	border: 0.0625rem rgb(var(--swatch-border-color)) solid;
	position: relative;
}

div.database_main .cell[data-label]::before {
	--wght: 900;
	content: attr(data-label) "\A";
	-webkit-hyphens: none;
	-moz-hyphens: none;
	-ms-hyphens: none;
	hyphens: none;
	/* label + newline */
	white-space: normal;
	/* makes newline work */
	font-family: var(--mono-font);
	font-variation-settings: 
		"wght" var(--mono-wght), 
		"slnt" var(--slnt), 
		"CASL" var(--CASL), 
		"CRSV" var(--CRSV), 
		"MONO" var(--MONO);
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-align: center;
	-webkit-align-items: center;
	-moz-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	-webkit-box-pack: start;
	-webkit-justify-content: flex-start;
	-moz-box-pack: start;
	-ms-flex-pack: start;
	justify-content: flex-start;
	font-size: 1em;
	text-transform: uppercase;
	width: -webkit-calc(100% - 0.7em);
	width: -moz-calc(100% - 0.7em);
	width: calc(100% - 0.7em);
	position: absolute;
	margin-bottom: 1em;
	top: 0;
	left: 0;
	background: #aaa;
	background: rgb(var(--swatch-primary-darkest));
	color: #fcfcfc;
	color: rgb(var(--swatch-menutxt-light-color));
	padding-left: 0.7em;
	padding-top: 0.35em;
	padding-bottom: 0.3em;
	font-weight: 900;
}

div.database_main > .database_title {
	--wght: 900;
	-ms-grid-row: 1;
	-ms-grid-column: 1;
	grid-area: title;
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	font-size: 2em;
	font-weight: 900;
	text-align: center;
	width: 100%;
	-webkit-box-align: center;
	-webkit-align-items: center;
	-moz-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	-moz-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	font-family: var(--header-font);
	letter-spacing: 0.025em;
	background: rgb(var(--swatch-primary-darkest));
	-webkit-border-top-right-radius: 0.5rem;
	-moz-border-radius-topright: 0.5rem;
	border-top-right-radius: 0.5rem;
	-webkit-border-top-left-radius: 0.5rem;
	-moz-border-radius-topleft: 0.5rem;
	border-top-left-radius: 0.5rem;
	color: rgb(var(--white-monochrome));
	border-bottom: 0.5rem double;
	margin-top: 0.5rem;
}

div.database_main > .database_table.top {
	-ms-grid-row: 2;
	-ms-grid-column: 1;
	grid-area: top;
	display: -ms-grid;
	display: grid;
		grid-template-areas:
		"group-name group-name"
		"aliases logotype"
		"leadership logotype";
	-ms-grid-columns: -webkit-calc(100% / 3) -webkit-calc((100% / 3) * 2);
	-ms-grid-columns: -moz-calc(100% / 3) -moz-calc((100% / 3) * 2);
	-ms-grid-columns: calc(100% / 3) calc((100% / 3) * 2);
	grid-template-columns: -webkit-calc(100% / 3) -webkit-calc((100% / 3) * 2);
	grid-template-columns: -moz-calc(100% / 3) -moz-calc((100% / 3) * 2);
	grid-template-columns: calc(100% / 3) calc((100% / 3) * 2);
	padding: 1rem;
	background-color: rgba(var(--black-monochrome));
}


div.database_main > .database_table.top > .cell:nth-of-type(1) {
	--wght: 900;
	-ms-grid-row: 1;
	-ms-grid-column: 1;
	-ms-grid-column-span: 2;
	grid-area: group-name;
	-ms-grid-column: 1;
	-ms-grid-column-span: 2;
	grid-column: 1 / span 2;
	font-size: 2rem;
	text-align: center;
	padding: 2.3rem 0.5rem 0.5rem 0.5rem;
	font-weight: 900;
}

div.database_main > .database_table.top > .cell:nth-of-type(1)::before {
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	-moz-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	font-size: 1rem;
	padding: 0.35rem 0.7rem 0.3rem;
	width: -webkit-calc(100% - 1.4rem);
	width: -moz-calc(100% - 1.4rem);
	width: calc(100% - 1.4rem);
}

div.database_main > .database_table.top > .cell:nth-of-type(2) {
	-ms-grid-row: 2;
	-ms-grid-column: 1;
	grid-area: aliases;
	-ms-grid-column: 1;
	-ms-grid-column-span: 1;
	grid-column: 1 / span 1;
}

div.database_main > .database_table.top > .cell:nth-of-type(3) {
	-ms-grid-row: 3;
	-ms-grid-column: 1;
	grid-area: leadership;
	-ms-grid-column: 1;
	-ms-grid-column-span: 1;
	grid-column: 1 / span 1;
}

div.database_main > .database_table.top > .hansarp_logo {
	-ms-grid-row: 2;
	-ms-grid-row-span: 2;
	-ms-grid-column: 2;
	height: 3rem;
	grid-area: logotype;
	display: -ms-grid;
	display: grid;
		grid-template-areas:
		"image caption"
		"image caption";
	-ms-grid-column: 2;
	grid-column: 2;
	height: 100%;
	width: auto;
	margin-left: 2rem;
}

div.database_main > .database_table.top > .hansarp_logo > .cell:first-of-type {
	-ms-grid-row: 1;
	-ms-grid-row-span: 2;
	-ms-grid-column: 1;
	grid-area: image;
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	position: relative;
	width: 9rem;
	height: 9rem;
	justify-self: end;
	border: 0.5rem double rgba(var(--white-monochrome));
}

div.database_main > .database_table.top > .hansarp_logo > .cell:first-of-type > img {
	background-color: rgba(var(--white-monochrome), 0.85);
	padding: 0.5em;
}

div.database_main > .database_table.top > .hansarp_logo > .cell:last-of-type {
	--wght: 900;
	-ms-grid-row: 1;
	-ms-grid-row-span: 2;
	-ms-grid-column: 2;
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-align: center;
	-webkit-align-items: center;
	-moz-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	grid-area: caption;
	height: 5rem;
	justify-self: start;
	-webkit-align-self: center;
	-ms-flex-item-align: center;
	align-self: center;
	padding: 1em 1em 1em 4em;
	margin-left: -3em;
	background-color: rgb(var(--swatch-primary-darkest));
	font-family: var(--mono-font);
	font-size: 1rem;
	font-weight: 900;
	hyphens: none;
}

div.database_main > .database_table.top > .hansarp_logo > .cell:last-of-type,
div.database_main > .database_table.top > .hansarp_logo > .cell:last-of-type * {
	font-variation-settings: 
		"wght" var(--mono-wght), 
		"slnt" var(--slnt), 
		"CASL" var(--CASL), 
		"CRSV" var(--CRSV), 
		"MONO" var(--MONO);
}

div.database_main > .database_table.main {
	-ms-grid-row: 3;
	-ms-grid-column: 1;
	grid-area: main;
	display: -ms-grid;
	display: grid;
}

div.database_main > .database_table.main > .locations {
	display: -ms-grid;
	display: grid;
		grid-template-areas:
		"primary addtl limited";
	-ms-grid-columns: 40% 40% 20%;
	grid-template-columns: 40% 40% 20%;
	margin-top: 1rem;
}

div.database_main > .database_table.main > .locations > .cell {
	position: relative;
}

div.database_main > .database_table.main > .locations > .cell::before {
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	-moz-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	font-size: 1rem;
	padding: 0.35rem 0.7rem 0.3rem;
	width: -webkit-calc(100% - 1.4rem);
	width: -moz-calc(100% - 1.4rem);
	width: calc(100% - 1.4rem);
	text-align: center;
}

div.database_main > .database_table.main > .locations > .cell:nth-of-type(1) {
	-ms-grid-row: 1;
	-ms-grid-column: 1;
	grid-area: primary;
}

div.database_main > .database_table.main > .locations > .cell:nth-of-type(2) {
	-ms-grid-row: 1;
	-ms-grid-column: 2;
	grid-area: addtl;
}

div.database_main > .database_table.main > .locations > .cell:nth-of-type(3) {
	-ms-grid-row: 1;
	-ms-grid-column: 3;
	grid-area: limited;
}

div.database_main > .database_table.main > .locations > .cell > ul {
	list-style: none;
	list-style-position: outside;
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-flex-wrap: wrap;
	-ms-flex-wrap: wrap;
	flex-wrap: wrap;
	position: relative;
	margin: 1.5rem 0 0 0;
	padding: 0;
}

div.database_main > .database_table.main > .locations > .cell > ul > li {
	width: 100%;
	padding: 0.25rem 0 0.25rem 0.25rem;
}

div.database_main > .database_table.main > .locations > .cell > ul > li:not(:first-of-type) {
	border-top: 0.125rem dashed rgba(var(--swatch-primary))
}

div.database_main .main_content {
	margin-top: 1rem;
	background-color: rgba(var(--swatch-primary), 0.15);
}

div.database_main .main_content .cell::before {
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	-moz-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	font-size: 1rem;
	padding: 0.35rem 0.7rem 0.3rem;
	width: -webkit-calc(100% - 1.4rem);
	width: -moz-calc(100% - 1.4rem);
	width: calc(100% - 1.4rem);
	text-align: center;
}

div.database_main .main_content .cell div {
	font-size: 1.1rem;
	line-height: 1.75;
	padding: 0 2em;
}

/* Doc 4 Artifacts */

div.collapsible-block:nth-child(5) > div:nth-child(2) > div:nth-child(2) > p:nth-child(1) {
	margin: 0;
}

.responsive_table {
	background-color: rgba(var(--swatch-primary-darkest));
	padding: 1rem;
}

.responsive_table br {
	display: none;
}

.responsive_table .table_data,
.responsive_table .table_header_data {
	vertical-align: top;
	text-align: left;
	margin: -0.0625rem 0 0 -0.0625rem;
	position: relative;
}

.responsive_table .table_row {
	display: -ms-grid;
	display: grid;
		grid-template-areas:
		"ID  Title Title Title "
		"Date Subject Subject Subject "
		"Location Subject Subject Subject "
		"Current Current Current Current ";
	-ms-grid-columns: 25% 75%;
	grid-template-columns: 25% 75%;
	padding: 0.375em;
	border: 0.0625rem #CCCCCC solid;
	border: 0.0625rem rgb(var(--swatch-border-color)) solid;
	margin-bottom: 1.250em;
	position: relative;
	background-color: rgba(var(--black-monochrome),0.5);
}

.responsive_table .table_data {
	padding: 1.95em 0.5em 0.5em 0.5em;
	border: 0.0625rem #CCCCCC solid;
	border: 0.0625rem rgb(var(--swatch-border-color)) solid;
	position: relative;
	background-color: rgb(var(--white-monochrome));
}

.responsive_table .table_row div:nth-of-type(1) {
	-ms-grid-row: 1;
	-ms-grid-column: 1;
	grid-area: ID;
}

.responsive_table .table_row div:nth-of-type(4) {
	-ms-grid-row: 3;
	-ms-grid-column: 1;
	grid-area: Location;
}

.responsive_table .table_row div:nth-of-type(2) {
	-ms-grid-row: 2;
	-ms-grid-column: 1;
	grid-area: Date;
}

.responsive_table .table_row div:nth-of-type(3) {
	-ms-grid-row: 1;
	-ms-grid-column: 2;
	-ms-grid-column-span: 3;
	grid-area: Title;
}

.responsive_table .table_row div:nth-of-type(5) {
	-ms-grid-row: 2;
	-ms-grid-row-span: 2;
	-ms-grid-column: 2;
	-ms-grid-column-span: 3;
	grid-area: Subject;
}

.responsive_table .table_row div:nth-of-type(6) {
	-ms-grid-row: 4;
	-ms-grid-column: 1;
	-ms-grid-column-span: 4;
	grid-area: Current;
}

/* Add labels */
.responsive_table .table_data::before {
	--wght: 700;
	content: attr(data-label) "\A";
	/* label + newline */
