<body>
    <div id="continue-button-id" class="continue-button">
     <a id="continue-link" href="http://scpko.wikidot.com/scp-4485/offset/3"></a>
     <div class="accept">클릭하여 승인 요청</div>
     <div class="supervisor">상사 승인</div>
     <br>
     <span>✔</span>
    </div>
    <script>
        var continueButton = document.getElementById("continue-button-id");  
        var continueLink = document.getElementById("continue-link");
        var to = null;     

        function mouseOver() {
            if(to){
                clearTimeout(to);
            }           
            to = setTimeout(function(){                 
                continueButton.classList.remove("active-hover");
                continueLink.classList.add("active-link");                    
            }, 5500);
        }

        function mouseOut() {
            if(to){
                clearTimeout(to);
            }
            continueLink.classList.remove("active-link");
        }        

        continueLink.addEventListener("mouseover", mouseOver);
        continueLink.addEventListener("mouseout", mouseOut);
    </script>
</body>

