	color: rgb(var(--white-monochrome));
	position: relative;
	padding-top: 0.25rem;
}

div.database_main, div.database_text {
	background-color: rgb(var(--swatch-primary-darkest), 0.5);
	margin: 0.5rem;
	padding: 0 0 0.75rem 0;
	display: -ms-grid;
	display: grid;
			grid-template-areas:
		"title"
		"top"
		"main";
	-ms-grid-columns: 100%;
	grid-template-columns: 100%;
	position: relative;
	left: 0;
	overflow-wrap: break-word;
	word-wrap: break-word;
	-webkit-hyphens: auto;
	-ms-hyphens: auto;
	-moz-hyphens: auto;
	hyphens: auto;
}

div.database_main > .database_title {
	--wght: 900;
	-ms-grid-row: 1;
	-ms-grid-column: 1;
	grid-area: title;
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	font-size: 2em;
	font-weight: 900;
	text-align: center;
	width: 100%;
	-webkit-box-align: center;
	-webkit-align-items: center;
	-moz-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	-moz-box-pack: center;
	-ms-flex-pack: center;
	justify-content: center;
	font-family: var(--header-font);
	letter-spacing: 0.025em;
	background: rgb(var(--swatch-primary-darkest));
	-webkit-border-top-right-radius: 0.5rem;
	-moz-border-radius-topright: 0.5rem;
	border-top-right-radius: 0.5rem;
	-webkit-border-top-left-radius: 0.5rem;
	-moz-border-radius-topleft: 0.5rem;
