		"MONO" var(--MONO);
}

.database_table_top br {
	display: none;
}

.database_table_top span {
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-align: center;
	-webkit-align-items: center;
	   -moz-box-align: center;
		-ms-flex-align: center;
			align-items: center;
