[[include theme:black-highlighter-theme]]
[[include :scp-wiki:theme:the-foundation-theme |dark=--]]]
[[module css]]
:root {
    --rating-module-text-color: var(--swatch-menutxt-light-color);
    --rating-module-button-color:  var(--swatch-menutxt-light-color);

     --body-font: "franklin-gothic-urw", 'NanumBarunCatsi', sans-serif;
     --header-font: "titling-gothic-fb-compressed", 'NanumBarunCatsi', sans-serif;
     --title-font: "hypatia-sans-pro", 'NanumBarunCatsi', sans-serif;

     --header-title: "재 단";
}

#page-content > div:nth-child(1) {
    padding: 0.75rem;
}

.page-rate-widget-box {
    margin: 0;
    background:
     linear-gradient(0deg,
     rgba(var(--black-monochrome),.55) 0,
     rgba(var(--swatch-primary-darkest),.45)) !important;
}
[[/module]]
[[module css]]
table.wiki-content-table {
    width: 100%;
    font-size: 1.5em;
    text-align: center;
    margin: 0 auto;
}

.document-main .footnote_block {
    --wght: 600;
    all: initial;
    display: -webkit-inline-box;
    display: -webkit-inline-flex;
    display: -moz-inline-box;
    display: -ms-inline-flexbox;
    display: inline-flex;
    font-family: var(--body-font);
    font-weight: 600;
    font-size: 0.8em;
    margin-left: -0.5em;
    margin-right: -0.5em;
}

.document-main .footnote_content,
.document-main .footnote_content * {
    --wght: 600;
    all: initial;
    display: block;
    position: absolute;
    right: -12rem;
    width: 8rem;
    line-height: 1.2;
    border: 0.125rem solid rgb(var(--swatch-primary));
    font-family: var(--body-font);
    font-weight: 600;
    padding: 1rem;
    text-shadow: initial;
    -webkit-filter: initial;
    filter: initial;
    color: rgb(var(--white-monochrome));
    -webkit-transition:
     opacity 600ms cubic-bezier(0.4, 0.0, 0.2, 1),
     -webkit-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition:
     opacity 600ms cubic-bezier(0.4, 0.0, 0.2, 1),
     -webkit-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
    -o-transition:
     opacity 600ms cubic-bezier(0.4, 0.0, 0.2, 1),
     -o-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
    -moz-transition:
     opacity 600ms cubic-bezier(0.4, 0.0, 0.2, 1),
     transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
     -moz-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition:
     opacity 600ms cubic-bezier(0.4, 0.0, 0.2, 1),
     transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition:
     opacity 600ms cubic-bezier(0.4, 0.0, 0.2, 1),
     transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
     -webkit-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
     -moz-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1),
     -o-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
    pointer-events: none
}

.document-main .footnote_num {
    --wght: 900;
    text-shadow: initial;
    font-weight: 900;
    color: rgb(var(--white-monochrome));
    cursor: pointer;
    -webkit-box-shadow: initial;
    -moz-box-shadow: initial;
    box-shadow: initial;
    -webkit-filter: unset;
    filter: unset;
    -webkit-transform: unset;
    -moz-transform: unset;
    -ms-transform: unset;
    -o-transform: unset;
    transform: unset;
    border-bottom: 0.0625rem dotted;
    line-height: 2;
    background-color: rgb(var(--link-color));
    height: 1em;
    width: 1em;
    margin-right: 0.25rem;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    z-index: 1;
}

.document-main .footnote_block .footnote_content::before {
    --wght: 900;
    content: " ";
    position: absolute;
    font-size: 1em;
    top: -0.5em;
    left: -0.5em;
    text-shadow: initial;
    font-weight: 900;
    color: rgb(var(--white-monochrome));
    cursor: pointer;
    -webkit-box-shadow: initial;
    -moz-box-shadow: initial;
    box-shadow: initial;
    -webkit-filter: unset;
    filter: unset;
    -webkit-transform: unset;
    -moz-transform: unset;
    -ms-transform: unset;
    -o-transform: unset;
    transform: unset;
    line-height: 2;
    background-color: rgb(var(--link-color));
    height: 1em;
    width: 1em;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    padding: 0.25em
}

.document-main .footnote_block.one .footnote_content::before {
    content: "1";
}

.document-main .footnote_num + span {
    -webkit-transform: translateX(-25%);
    -moz-transform: translateX(-25%);
    -ms-transform: translateX(-25%);
    -o-transform: translateX(-25%);
    transform: translateX(-25%);
    opacity: 0;
}

.document-main .footnote_num:hover + span {
    -webkit-transform: translateX(0);
    -moz-transform: translateX(0);
    -ms-transform: translateX(0);
    -o-transform: translateX(0);
    transform: translateX(0);
    opacity: 1;
}

@media only screen and (max-width: 1050px) {

    .document-main .footnote_content {
     position: fixed;
     bottom: 1em;
     left: 12.5%;
     background-color: rgba(var(--white-monochrome));
     width: 75%;
    }
}

/* collapsible block */

#page-content .collapsible-block {
    display: block;
    text-align: center;
    padding: 0.75em;
    background-color: rgba(var(--gray-monochrome),0.1);
    border-top: 0.125rem dashed rgba(var(--dark-accent),1);
    border-bottom: 0.125rem dashed rgba(var(--dark-accent),1);
}

#page-content .collapsible-block .collapsible-block-content {
    text-align: left;
}

blockquote, .blockquote {
    background-color: rgb(var(--swatch-menubg-dark-color));
}

@media only screen and (max-width: 768px) {

    table.wiki-content-table tr {
     -webkit-flex-wrap: wrap;
     -ms-flex-wrap: wrap;
     flex-wrap: wrap;
    }

    table.wiki-content-table tr > td {
     min-width: 100%;
     font-size: 1rem;
     padding: 0.5rem;
    }
}

.document-main > br {
    display: none;
}

.document-main > div {
    display: block;
    width: -webkit-calc(100% - 3rem);
    width: -moz-calc(100% - 3rem);
    width: calc(100% - 3rem);
    background-color: rgb(var(--very-light-gray-monochrome), 1);
    padding: 0.75rem 0.75rem 0.5rem;
    margin: 1.5rem 0.5rem 0.5rem 0.5rem;
    border: 0.25rem rgb(var(--swatch-border-color)) solid;
    position: relative;
    line-height: 1.5;
    overflow-wrap: break-word;
    word-wrap: break-word;
    -webkit-hyphens: auto;
    -ms-hyphens: auto;
    -moz-hyphens: auto;
    hyphens: auto;
    font-size: inherit;
}

.document-main > div > p:last-of-type {
    margin-bottom: 0;
}

.document-main > div::before {
    --wght: 600;
    content: " ";
    position: absolute;
    top: -1.25rem;
    left: -0.5rem;
    padding: 0.5em 0.5em;
    color: rgb(var(--white-monochrome));
    font-size: -webkit-calc(12px + (14 - 12) * ( (100vw - 300px) / ( 800 - 300) ));
    font-size: -moz-calc(12px + (14 - 12) * ( (100vw - 300px) / ( 800 - 300) ));
    font-size: calc(12px + (14 - 12) * ( (100vw - 300px) / ( 800 - 300) ));
    font-weight: 600;
    letter-spacing: 0.05em;
    text-indent: 0;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    line-height: 1;
    white-space: nowrap;
}

.document-main > div.sp-containment,
.document-main > div.containment,
.document-main > div.description {
    background-color: rgba(var(--light-gray-monochrome),0.15);
    border-color: rgba(var(--swatch-primary-darkest),1);
}

.document-main > div.sp-containment::before,
.document-main > div.containment::before,
.document-main > div.description::before {
    background-color: rgba(var(--swatch-primary-darkest),1)
}

.document-main > div.sp-containment::before {
    content: "특수 격리 절차";    
}

.document-main > div.containment::before {
    content: "격리 사양";    
}

.document-main > div.description::before {
    content: "설명";
}

.document-main > .sp-containment > .sp-containment-image {
  grid-area: image;
  width: 15vw;
  float: right;
  padding-left: 1rem;
}

.document-main > .sp-containment > .sp-containment-image img {
  max-height: 110vh;
}

.scp-image-block {
    -webkit-box-shadow: initial;
       -moz-box-shadow: initial;
     box-shadow: initial;
}

.scp-image-block .scp-image-caption {
    background-color: rgb(var(--swatch-menubg-dark-color));
}

.containment-image {
    margin-top: 1rem;
}

.document-main > .sp-containment .scp-image-block img {
    max-height: 90vh;
}

/* Doc 3 GoI Entry */

div.database_container {
    width: 100%;
    color: rgb(var(--white-monochrome));
    position: relative;
    padding-top: 0.25rem;
}

div.database_main, div.database_text {
    background-color: rgb(var(--swatch-primary-darkest), 0.5);
    margin: 0.5rem;
    padding: 0 0 0.75rem 0;
    display: -ms-grid;
    display: grid;
     grid-template-areas:
     "title"
     "top"
     "main";
    -ms-grid-columns: 100%;
    grid-template-columns: 100%;
    position: relative;
    left: 0;
    overflow-wrap: break-word;
    word-wrap: break-word;
    -webkit-hyphens: auto;
    -ms-hyphens: auto;
    -moz-hyphens: auto;
    hyphens: auto;
}

div.database_main > .database_title {
    --wght: 900;
    -ms-grid-row: 1;
    -ms-grid-column: 1;
    grid-area: title;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    font-size: 2em;
    font-weight: 900;
    text-align: center;
    width: 100%;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
    font-family: var(--header-font);
    letter-spacing: 0.025em;
    background: rgb(var(--swatch-primary-darkest));
    -webkit-border-top-right-radius: 0.5rem;
    -moz-border-radius-topright: 0.5rem;
    border-top-right-radius: 0.5rem;
    -webkit-border-top-left-radius: 0.5rem;
    -moz-border-radius-topleft: 0.5rem;
    border-top-left-radius: 0.5rem;
    color: rgb(var(--white-monochrome));
    border-bottom: 0.5rem double;
}

.database_table_top {
    font-size: 0.75em;
    font-family: var(--mono-font);
    padding: 1rem 0.5rem 0 0.5rem;
    display: -ms-grid;
    display: grid;
     grid-template-areas:
     "date secure"
     "date recognize";
    -ms-grid-columns: 1fr auto auto;
    grid-template-columns: 1fr auto auto;
    -ms-grid-rows: 1fr 1fr;
    grid-template-rows: 1fr 1fr;
}

.database_table_top,
.database_table_top * {
    font-variation-settings:
     "wght" var(--mono-wght),
     "slnt" var(--slnt),
     "CASL" var(--CASL),
     "CRSV" var(--CRSV),
     "MONO" var(--MONO);
}

.database_table_top br {
    display: none;
}

.database_table_top span {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
     -ms-flex-align: center;
     align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
     -ms-flex-pack: center;
     justify-content: center;
    text-align: center;
    padding: 1em;
    white-space: wrap;
    padding-bottom: 1em;
    background-color: rgba(var(--light-gray-monochrome),0.25);
    border: 0.125rem solid rgba(var(--light-gray-monochrome),0.5);
}

.database_table_top span:nth-of-type(1) {
    grid-area: date;
    font-size: 1rem;
}

.database_table_top span:nth-of-type(2) {
    grid-area: secure;
}

.database_table_top span:nth-of-type(3) {
    grid-area: recognize;
}

div.database_text {
    padding: 1rem;
    font-family: var(--mono-font);
}

div.database_text,
div.database_text * {
    font-variation-settings:
     "wght" var(--mono-wght),
     "slnt" var(--slnt),
     "CASL" var(--CASL),
     "CRSV" var(--CRSV),
     "MONO" var(--MONO);
}

.database_memo {
    font-family: var(--body-font);
    padding: 1rem;
    margin: 1rem 0;
    background-color: rgb(var(--pale-gray-monochrome),0.25);
    font-size: 1.15em;
    border: 0.125rem dashed rgba(var(--dark-gray-monochrome),1);
}

.database_memo p:first-of-type {
    margin-top: 0;
}

@media only screen and (max-width: 768px) {
    .document-main {
     margin: 0;
     padding: 0;
    }

    .document-main > .sp-containment .scp-image-block,
    .document-main > .sp-containment .scp-image-caption,
    .document-main > .sp-containment > .sp-containment-image,
    .document-main > .sp-containment > .sp-containment-text {
     clear: none !important;
    }

    .document-main > .sp-containment .scp-image-block.block-right {
     max-width: 30vw;
     float: initial;
     margin: 0;
    }

    .document-main > .sp-containment > .sp-containment-image {
    width: initial;
    }

    .document-main > .sp-containment > .sp-containment-image img {
     -o-object-fit: cover;
        object-fit: cover;
     -o-object-position: bottom;
        object-position: bottom;
    }
    .database_table_top {
     grid-template-areas:
     "date"
     "secure"
     "recognize";
     -ms-grid-columns: 1fr;
     grid-template-columns: 1fr;
     -ms-grid-rows: 1fr 1fr 1fr;
     grid-template-rows: 1fr 1fr 1fr;
    }
}
