[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Caveat&display=swap');
@import url('https://fonts.googleapis.com/css?family=Cinzel');
@import url('https://fonts.googleapis.com/css?family=Anton');
@import url('https://fonts.googleapis.com/css?family=Pinyon+Script');
@import url('https://fonts.googleapis.com/css?family=Pangolin&display=swap');
@import url('https://fonts.googleapis.com/css?family=Patrick+Hand+SC&display=swap');
@import url('https://fonts.googleapis.com/css?family=Reenie+Beanie');

.page {
    display: block;
    overflow: hidden;
    font-family: 'Pangolin', cursive;
    font-size: 1.6em;
    font-style: normal;


    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;


    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }
.page p {
    line-height: 20px;
    margin: 0;
}
.page ul {
    list-style: symbols;
}

#page-content {
    position: relative;
}

.desktop-only {
    height: 21rem;
    width: 10rem;
    background: #ffffff;
    position: -webkit-sticky;
    position: sticky;
    top: 15rem;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    border: solid 1px #444444;
    padding: 10px;
    -webkit-box-shadow: none;
       -moz-box-shadow:  none;
            box-shadow: none;
    overflow: visible;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
       -moz-box-orient: vertical;
       -moz-box-direction: normal;
        -ms-flex-direction: column;
            flex-direction: column;
    -webkit-transform: translateX(400%);
       -moz-transform: translateX(400%);
        -ms-transform: translateX(400%);
         -o-transform: translateX(400%);
            transform: translateX(400%);
}

.article {
    margin-top: -21rem!important;
}

@media (max-width: 1250px) {
    .desktop-only {
     display: none !important;
     visibility:hidden
    }
.article {
    margin-top: 4rem!important;
}
    #page-content > div:nth-child(5) {
     margin-top: 0;
    }

} 
[[/module]]
