  text-decoration: underline;
}
.hover .hover-text {
   position: absolute;
   display: none;
   margin: 20px 0px;
   height: auto;
   width: auto;
   background:white;
   border: 1px solid black;
   color: black;
   padding: 0.5em;
}

.hover:hover {
  text-decoration: none;
  border-bottom: none;
  color: #004c00;
}
.hover:hover .hover-text {
  display: inline;
}
