[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Oxanium&display=swap');
@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Mono:wght@600&display=swap');
@import url('https://fonts.googleapis.com/cssjune?family=Anton');
@import url('https://fonts.googleapis.com/css2?family=Staatliches&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Share+Tech+Mono&display=swap');


:root{
--red: 188, 54, 33;
--medium-accent: var(--red);
--cutColour: #fafafa;
--barColour: var(--red);
--toggle-border-color: rgb(var(--red));
--toggle-icon-color: rgb(var(--red));
--toggle-roundness: 0;
--mono-font: 'IBM Plex Mono', monospace;
--background-gradient-distance: 20rem;
--background-gradient-color: var(--red);
}

/* CN Fix */
:root:lang(cn) {
    --header-title: "REDTAPE";
    --header-subtitle: "离午夜还有21分钟";
}

.goc {
    background: #fff url('https://scp-wiki.wdfiles.com/local--files/theme%3Agoc/goc-fade.png') -5rem -5rem no-repeat!important;
    border-left-color: #275495!important;
    background-size: 30rem;
}
.goc hr {
    background: #275495!important;
    border-color: #fff!important;
}
.goc .scp-image-block{
    border-color: #275495;
    box-shadow: none;
}
div.parapost {
    background:  black;
    border-width: 2px;
    border-style: solid;
    border-color: #1a1a1a;
    padding: 0 1em;
    margin: 1em 3em;
    font-family: var(--mono-font);
}

div.parapost hr {
    border-top: 0.15rem dashed white !important;
    background: transparent !important;
    border-left: none !important;
    border-right: none !important;
}

@media (max-width: 479px) {
   div.parapost {margin: 1em 0;}
}

@media (min-width: 480px) and (max-width: 580px) {
   div.parapost {margin: 0.5em;}
}

.revs {
     border: solid 3px lightgray;
     background: rgb(var(--dark-gray-monochrome));
     padding:0.5rem 1rem;
     font-weight: bold;
}

.revs a:visited, .revs a {
     color:red;
     filter: drop-shadow(0px 0px 0.05rem red);
     font-family: 'Oxanium', cursive;
}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}

table.wiki-content-table th {
    background: rgb(var(--red));
    text-align: center;
}

#page-title {
    text-align: center;
}
.collapsible-block {
    display: block;
    text-align: center;
    padding: 0.75em;
    background-color: #dfdfdf;
    border-top: 0.125rem solid crimson;
    border-bottom: 0.125rem solid crimson;
}

.collapsible-block .collapsible-block-content {
    text-align: left;
}

.licensebox > .collapsible-block {
    background-color: transparent;
    border: none;
}

ul {
    list-style: disclosure-open;
}

.addendum {
   background: #EAEAEA;
   border: none;
   border-left: 6px solid rgb(var(--red));
    width:85%;
    padding:1px 15px;
    margin: 10px auto;
}

#main-content .page-tags span {
    justify-content: flex-start;
}

#main-content .page-tags span a:-webkit-any-link:not([href^="/system:page-tags/tag/_"]) {
    --wght: 500;
    display: inline;
    position: relative;
    flex-grow: 0;
    width: -webkit-max-content;
    width: max-content;
    height: 1.175em;
    margin: 0 0 0 0.5125rem;
    padding: 0.1875rem 0.3125rem 0.1875rem 1rem;
    border-top-right-radius: 0.25rem;
    border-bottom-right-radius: 0.25rem;
    background: rgb(var(--swatch-menubg-dark-color));
    color: rgb(var(--swatch-menutxt-light-color));
    font-size: 0.6875rem;
    line-height: 0.8125rem;
    text-decoration: none;
    -webkit-clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
            clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
}

#main-content .page-tags span a:-moz-any-link:not([href^="/system:page-tags/tag/_"]) {
    --wght: 500;
    display: inline;
    position: relative;
    flex-grow: 0;
    width: -moz-max-content;
    width: max-content;
    height: 1.175em;
    margin: 0 0 0 0.5125rem;
    padding: 0.1875rem 0.3125rem 0.1875rem 1rem;
    border-top-right-radius: 0.25rem;
    border-bottom-right-radius: 0.25rem;
    background: rgb(var(--swatch-menubg-dark-color));
    color: rgb(var(--swatch-menutxt-light-color));
    font-size: 0.6875rem;
    line-height: 0.8125rem;
    text-decoration: none;
    clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
}

#main-content .page-tags span a:any-link:not([href^="/system:page-tags/tag/_"]) {
    --wght: 500;
    display: inline;
    position: relative;
    flex-grow: 0;
    width: -webkit-max-content;
    width: -moz-max-content;
    width: max-content;
    height: 1.175em;
    margin: 0 0 0 0.5125rem;
    padding: 0.1875rem 0.3125rem 0.1875rem 1rem;
    border-top-right-radius: 0.25rem;
    border-bottom-right-radius: 0.25rem;
    background: rgb(var(--swatch-menubg-dark-color));
    color: rgb(var(--swatch-menutxt-light-color));
    font-size: 0.6875rem;
    line-height: 0.8125rem;
    text-decoration: none;
    -webkit-clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
            clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
}

#main-content .page-tags span a:hover:not([href^="/system:page-tags/tag/_"]),
#main-content .page-tags span a:active:not([href^="/system:page-tags/tag/_"]) {
    color: rgb(var(--swatch-menutxt-light-color));
}

#main-content .page-tags span a:focus-within:not([href^="/system:page-tags/tag/_"]) {
    color: rgb(var(--swatch-menutxt-dark-color));
}

#main-content .page-tags span a:-webkit-any-link:not([href^="/system:page-tags/tag/_"])::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 0.5rem;
    width: 0.25rem;
    height: 0.25rem;
    transform: translateY(-50%);
    border-radius: 50%;
    background: rgb(var(--swatch-background));
    box-shadow: -0.0625rem -0.0625rem 0.125rem rgb(var(--swatch-menubg-dark-color));
}

#main-content .page-tags span a:-moz-any-link:not([href^="/system:page-tags/tag/_"])::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 0.5rem;
    width: 0.25rem;
    height: 0.25rem;
    transform: translateY(-50%);
    border-radius: 50%;
    background: rgb(var(--swatch-background));
    box-shadow: -0.0625rem -0.0625rem 0.125rem rgb(var(--swatch-menubg-dark-color));
}

#main-content .page-tags span a:any-link:not([href^="/system:page-tags/tag/_"])::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 0.5rem;
    width: 0.25rem;
    height: 0.25rem;
    transform: translateY(-50%);
    border-radius: 50%;
    background: rgb(var(--swatch-background));
    box-shadow: -0.0625rem -0.0625rem 0.125rem rgb(var(--swatch-menubg-dark-color));
}

.page-tags span {
    border-top: .5rem solid transparent;
}

div.banner {
   display: grid;
   grid-template-columns: repeat(2, 50fr);
   grid-template-rows: auto;
   grid-gap: 1rem;
   grid-template-areas:
     "head head"
     "info image";
}

div.bannerhead {
    grid-area: head;
    background: rgb(var(--red));
    color: white;
    font-family: 'Staatliches', cursive;
    font-size: 5em;
    text-align: center;
    padding: 0;
    position: relative;
    z-index: 1;
    border-left: 12px double #fcfcfc;
    box-shadow: 5px 5px rgba(1,1,1,.25);
    line-height: 1.2;
}

div.bannerinfo{
    grid-area: info;
    background: rgb(var(--red));
    color: white;
    font-family: 'Share Tech Mono', monospace;
    padding: 0.75rem;
    font-size: 1.5em;
    position: relative;
    z-index: 1;
    border-left: 12px double #fcfcfc;
    box-shadow: 5px 5px rgba(1,1,1,.25);
}

div.bannerinfo hr {
    border-top: 0.15rem dashed white !important;
    background: transparent !important;
    border-left: none !important;
    border-right: none !important;
}

div.bannerimage{
    grid-area: image;
    height: min-content;
    position: relative;
    z-index: 0;
}

div.bannerimage img {
  z-index: 0;
  animation: spin 15s cubic-bezier(1,1,1,1) infinite;
filter: drop-shadow(0 0 0.15rem black);
}

#page-content hr {
border-top: none;
height: .07em;
background: crimson;
border-left: 50px double #fcfcfc;
border-right: 50px double #fcfcfc;
}

.addendum hr {
border-color: #eaeaea!important;
}

.scp-image-block {
border: none;
border-bottom: 2px solid crimson;
    box-shadow: none;
}


@keyframes spin {
   100% { transform: rotate(360deg); }
}

@media only screen and (max-width: 520px) {
div.banner {
   display: grid;
   grid-template-columns: 1fr;
   grid-template-rows: 20fr auto;
   grid-template-areas:
     "head"
     "info"
     "image";
}
div.bannerinfo, div.bannerhead {
    opacity: 0.9;
}
div.bannerimage {
    position: absolute;
}
