[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Mono:wght@600&display=swap');
@import url('https://fonts.googleapis.com/css?family=Anton');
@import url('https://fonts.googleapis.com/css2?family=Staatliches&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Share+Tech+Mono&display=swap');

:root{
 --red: 128, 34, 25;
--mono-font: 'IBM Plex Mono', monospace;
--toggle-icon-color: rgb(var(--red));
--toggle-roundness: 0;
}

/* CN Fix */
:root:lang(cn) {
    --header-title: "REDTAPE";
    --header-subtitle: "离午夜还有21分钟";
}

#page-title {
    text-align: center!important;
}


.scp-image-block {
border: none;
border-bottom: 2px solid crimson;
box-shadow: none;
}

.secret, .secret a, .secret a:visited {
    color: #1E1E1E!important;
}

#page-content a:not([href*="user"]):not([href*="javascript:;"]):not([href="/classification-committee-memo"]) {
    box-shadow: none;
}

.table.wiki-content-table td {
    border: 1px solid #802219;
}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}


div.objclass::before {
   filter: invert(1);
}

.collapsible-block {
    display: block;
    text-align: center;
    padding: 0.75em;
    background-color: #2f2f2f;
    border-top: 0.125rem solid crimson;
    border-bottom: 0.125rem solid crimson;
}

.collapsible-block .collapsible-block-content {
    text-align: left;
}

.addendum {
    background: #1E1E1E;
    border: none;
    border-left: 6px solid rgb(var(--red));
    color: white!important;
    width:85%;
    padding:1px 15px;
    margin: 10px auto;
}

.licensebox blockquote {
    background-color: #000;
    border: none;
    border-left: 6px solid rgb(var(--red));
}

#main-content .page-tags span {
    justify-content: flex-start;
}

#main-content .page-tags span a:-webkit-any-link:not([href^="/system:page-tags/tag/_"]) {
    --wght: 500;
    display: inline;
    position: relative;
    flex-grow: 0;
    width: -webkit-max-content;
    width: max-content;
    height: 1.175em;
    margin: 0 0 0 0.5125rem;
    padding: 0.1875rem 0.3125rem 0.1875rem 1rem;
    border-top-right-radius: 0.25rem;
    border-bottom-right-radius: 0.25rem;
    background: rgb(var(--swatch-menubg-dark-color));
    color: rgb(var(--swatch-menutxt-light-color));
    font-size: 0.6875rem;
    line-height: 0.8125rem;
    text-decoration: none;
    -webkit-clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
            clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
}

#main-content .page-tags span a:-moz-any-link:not([href^="/system:page-tags/tag/_"]) {
    --wght: 500;
    display: inline;
    position: relative;
    flex-grow: 0;
    width: -moz-max-content;
    width: max-content;
    height: 1.175em;
    margin: 0 0 0 0.5125rem;
    padding: 0.1875rem 0.3125rem 0.1875rem 1rem;
    border-top-right-radius: 0.25rem;
    border-bottom-right-radius: 0.25rem;
    background: rgb(var(--swatch-menubg-dark-color));
    color: rgb(var(--swatch-menutxt-light-color));
    font-size: 0.6875rem;
    line-height: 0.8125rem;
    text-decoration: none;
    clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
}


#main-content .page-tags span a:any-link:not([href^="/system:page-tags/tag/_"]) {
    --wght: 500;
    display: inline;
    position: relative;
    flex-grow: 0;
    width: -webkit-max-content;
    width: -moz-max-content;
    width: max-content;
    height: 1.175em;
    margin: 0 0 0 0.5125rem;
    padding: 0.1875rem 0.3125rem 0.1875rem 1rem;
    border-top-right-radius: 0.25rem;
    border-bottom-right-radius: 0.25rem;
    background: rgb(var(--swatch-menubg-dark-color));
    color: rgb(var(--swatch-menutxt-light-color));
    font-size: 0.6875rem;
    line-height: 0.8125rem;
    text-decoration: none;
    -webkit-clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
            clip-path: polygon(0.5rem 0, 100% 0, 100% 100%, 0.5rem 100%, 0 50%);
}

#main-content .page-tags span a:hover:not([href^="/system:page-tags/tag/_"]),
#main-content .page-tags span a:active:not([href^="/system:page-tags/tag/_"]) {
    color: rgb(var(--swatch-menutxt-light-color));
}

#main-content .page-tags span a:focus-within:not([href^="/system:page-tags/tag/_"]) {
    color: rgb(var(--swatch-menutxt-dark-color));
}

#main-content .page-tags span a:-webkit-any-link:not([href^="/system:page-tags/tag/_"])::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 0.5rem;
    width: 0.25rem;
    height: 0.25rem;
    transform: translateY(-50%);
    border-radius: 50%;
    background: rgb(var(--swatch-background));
    box-shadow: -0.0625rem -0.0625rem 0.125rem rgb(var(--swatch-menubg-dark-color));
}

#main-content .page-tags span a:-moz-any-link:not([href^="/system:page-tags/tag/_"])::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 0.5rem;
    width: 0.25rem;
    height: 0.25rem;
    transform: translateY(-50%);
    border-radius: 50%;
    background: rgb(var(--swatch-background));
    box-shadow: -0.0625rem -0.0625rem 0.125rem rgb(var(--swatch-menubg-dark-color));
}

#main-content .page-tags span a:any-link:not([href^="/system:page-tags/tag/_"])::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 0.5rem;
    width: 0.25rem;
    height: 0.25rem;
    transform: translateY(-50%);
    border-radius: 50%;
    background: rgb(var(--swatch-background));
    box-shadow: -0.0625rem -0.0625rem 0.125rem rgb(var(--swatch-menubg-dark-color));
}

.page-tags span {
    border-top: .5rem solid transparent;
}

div.banner {
   display: grid;
   grid-template-columns: repeat(2, 50fr);
   grid-template-rows: auto;
   grid-gap: 1rem;
   grid-template-areas:
     "head head"
     "info image";
}

div.bannerhead {
    grid-area: head;
    background: rgb(var(--red));
    color: white;
    font-family: 'Staatliches', cursive;
    font-size: 5em;
    text-align: center;
    padding: 0;
    position: relative;
    z-index: 1;
    border-left: 12px double rgb(var(--swatch-background));
    box-shadow: 5px 5px rgba(1,1,1,.25);
    line-height: 1.2;
}

div.bannerinfo{
    grid-area: info;
    background: rgb(var(--red));
    color: white;
    font-family: 'Share Tech Mono', monospace;
    padding: 0.75rem;
    font-size: 1.5em;
    position: relative;
    z-index: 1;
    border-left: 12px double rgb(var(--swatch-background));
    box-shadow: 5px 5px rgba(1,1,1,.25);
}

div.bannerinfo hr {
    border-top: 0.15rem dashed white !important;
    background: transparent !important;
    border-left: none !important;
    border-right: none !important;
}

div.bannerimage{
    grid-area: image;
    height: min-content;
    position: relative;
    z-index: 0;
}

div.bannerimage img {
  z-index: 0;
  animation: spin 15s cubic-bezier(1,1,1,1) infinite;
filter: drop-shadow(0 0 0.15rem black);
}

@keyframes spin {
   100% { transform: rotate(360deg); }
}

@media only screen and (max-width: 520px) {
div.banner {
   display: grid;
   grid-template-columns: 1fr;
   grid-template-rows: 20fr auto;
   grid-template-areas:
     "head"
     "info"
     "image";
}

div.bannerinfo, div.bannerhead {
    opacity: 0.9;
}

div.bannerimage {
    position: absolute;
}

div.scp-image-block .scp-image-caption {
    background: rgb(var(--swatch-background));
    color: rgb(var(--red));
    box-shadow: none;
}

div.bannerimage img {
    filter: drop-shadow(0 0 0.15rem rgb(var(--red)));
}
div.obj {
background-color: none;
background-image: url("data:image/svg+xml,%3Csvg id='patternId' width='100%25' height='100%25' xmlns='http://www.w3.org/2000/svg'%3E%3Cdefs%3E%3Cpattern id='a' patternUnits='userSpaceOnUse' width='29' height='50.115' patternTransform='scale(1) rotate(0)'%3E%3Crect x='0' y='0' width='100%25' height='100%25' fill='hsla(0, 100%25, 22%25, 1)'/%3E%3Cpath d='M14.5 6.628L8.886 3.372v-6.515L14.502-6.4l5.612 3.257-.001 6.514zm0 50.06l-5.613-3.256v-6.515l5.614-3.258 5.612 3.257-.001 6.515zm14.497-25.117l-5.612-3.257v-6.515L29 18.541l5.612 3.257-.001 6.515zm-29 0l-5.612-3.257v-6.515L0 18.541l5.612 3.257v6.515zM14.5 11.82L4.36 5.967l.002-11.706 10.14-5.855L24.638-5.74l-.001 11.707zm0 50.06L4.36 56.028l.002-11.706 10.14-5.855 10.137 5.852-.001 11.707zm14.498-25.118L18.858 30.91l.002-11.707L29 13.349l10.137 5.853-.001 11.706zm-29 0l-10.139-5.852.002-11.707L0 13.349l10.138 5.853-.002 11.706zm14.501-19.905L0 8.488.002-8.257l14.5-8.374L29-8.26l-.002 16.745zm0 50.06L0 58.548l.002-16.745 14.5-8.373L29 41.8l-.002 16.744zM28.996 41.8l-14.498-8.37.002-16.744L29 8.312l14.498 8.37-.002 16.745zm-29 0l-14.498-8.37.002-16.744L0 8.312l14.498 8.37-.002 16.745z' stroke-linecap='square' stroke-width='1' stroke='hsla(4, 100%25, 0%25, 1)' fill='none'/%3E%3C/pattern%3E%3C/defs%3E%3Crect width='800%25' height='800%25' transform='translate(0,0)' fill='url(%23a)'/%3E%3C/svg%3E");
