div.banner {
   display: grid;
   grid-template-columns: repeat(2, 50fr);
   grid-template-rows: auto;
   grid-gap: 1rem;
   grid-template-areas:
     "head head"
     "info image";
}

div.bannerhead {
    grid-area: head;
    background: rgb(var(--red));
    color: white;
    font-family: 'Staatliches', '꺾깎체', cursive;
    font-size: 5em;
    text-align: center;
    padding: 0;
    position: relative;
    z-index: 1;
    border-left: 12px double rgb(var(--swatch-background));
    box-shadow: 5px 5px rgba(1,1,1,.25);
    line-height: 1.2;
}

div.bannerinfo{
    grid-area: info;
    background: rgb(var(--red));
    color: white;
    font-family: 'Share Tech Mono', 'UnJamoDotum', monospace;
    padding: 0.75rem;
    font-size: 1.5em;
    position: relative;
    z-index: 1;
    border-left: 12px double rgb(var(--swatch-background));
    box-shadow: 5px 5px rgba(1,1,1,.25);
}

div.bannerinfo hr {
    border-top: 0.15rem dashed white !important;
    background: transparent !important;
    border-left: none !important;
    border-right: none !important;
}

div.bannerimage{
    grid-area: image;
    height: min-content;
    position: relative;
    z-index: 0;
}

div.bannerimage img {
  z-index: 0;
  animation: spin 15s cubic-bezier(1,1,1,1) infinite;
filter: drop-shadow(0 0 0.15rem black);
}

@keyframes spin {
   100% { transform: rotate(360deg); }
}

@media only screen and (max-width: 520px) {
div.banner {
   display: grid;
   grid-template-columns: 1fr;
   grid-template-rows: 20fr auto;
   grid-template-areas:
     "head"
     "info"
     "image";
}

div.bannerinfo, div.bannerhead {
    opacity: 0.9;
}

div.bannerimage {
    position: absolute;
}

div.scp-image-block .scp-image-caption {
    background: rgb(var(--swatch-background));
    color: rgb(var(--red));
    box-shadow: none;
}

div.bannerimage img {
    filter: drop-shadow(0 0 0.15rem rgb(var(--red)));
}
