[[include :scp-wiki:theme:basalt hidetitle=a|darkmode=a]]
[[module CSS]]
body {
    background-image: url(http://scp-jp-storage.wikidot.com/local--files/file:2375337-1046-s18m/LustBackground.png);
    background-attachment: fixed;
    background-size: cover;
}


:root {
    /* REDMODE */
/*hover boarder*/
    --basalt-UI-dark-palette: 215, 45, 45;
/*links and highlights*/
    --basalt-bright-element-color: 215, 45, 40;
/*Background*/
    --basalt-primary-color: 247, 115, 155;
/*header footer block*/
    --basalt-secondary-color: 127, 25, 54;
/*footerboarder, sideline*/
    --basalt-tertiary-color: 155, 40, 40;
    --general-border-color: 255, 51, 102;
}

:root {

    --logo: url("http://scp-jp-storage.wikidot.com/local--files/file:2375337-1052-wncw/Lust_Icon.png");
    --header-title: " L U S T ";
    --header-subtitle: "いついつまでもご逗留を";
}

[[/module]]
