[[module CSS]]
div[class$="_memo"] {
    background: var(--memo-logo) rgba(var(--memo-color), 1);
    background-size: contain;
    background-position: top center;
    background-repeat: no-repeat;
    min-height: 4rem;
    margin-inline: auto;
    margin-block: 2rem;
    padding-inline: 20px;
    padding-block: 10px;
    border: solid 2px rgba(0, 0, 0, 0.1);
    text-align: center;
}

div[class$="_memo"]::before {
    content: var(--memo-heading);
    font-family: var(--UI-font);
    font-weight: 900;
    font-size: 1.3em;
    text-align: center;
    margin: 0 auto 1rem auto;
    display: flex;
    flex-direction: column;
    padding-top: 0.5em;
}

div.raisa_memo {
    --memo-heading: "NOTICE FROM THE RECORDS AND INFORMATION SECURITY ADMINISTRATION";
    --memo-logo: url(https://scp-wiki.wikidot.com/local--files/theme:basalt/RAISA_LIGHTLOGO.png);
    --memo-color: 255, 243, 173;
}
[[/module]]

[[module css]]
.footnotes-footer {
display:none;
}
[[/module]]

[[module CSS]]
    :root {
        --header-gradient-color-bottom: 157, 157, 157;
    --bright-accent: 190, 190, 190;
    --pale-accent: 120, 120, 120;
    --dark-accent: 141, 199, 223;
    --hover-link-color: 91, 206, 250;
    --visited-link-color: 91, 206, 250;
    --footnotes-footer-num-color: 120, 120, 120;
    --hoverblock-header-bg:  120, 120, 120;}
#page-title {display: none;}
[[/module]]

[[module css]]
.danke {
    padding: 5px;
    margin-bottom:10px;
    font-family: monospace;
    font-size: 1.1em;
}

.agent {
    background-color:#102240;
    border: 3px solid #5BCDFA;
    color: #5BCDFA;
}
[[/module]]

[[module CSS]]
.bang {
  padding: 0px;
  width: 15px;
  transform: scale(50);
  pointer-events: none;
}
[[/module]]

[[module css]]
.page {
    display: block;
    overflow: hidden;
    font-family: "Monotype Corsiva", "Bradley Hand ITC", sans-serif;
    font-style: normal;
    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;
    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;
    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
}
.page p,
.page ul {
    line-height: 20px;
    margin: 0;
}
[[/module]]

