[[module CSS]]

:root {

--admo-backdrop: url(https://scp-wiki.wikidot.com/local--files/azamo/6488-bg.webp);

--admo-color-bright: 90, 170, 171;
--admo-color-dim: 2, 20, 24;
--admo-color-dark: 0, 10, 11;
--admo-color-alt: 84, 156, 148;

--general-border-color: 30, 50, 47;
--basalt-tertiary-color: 8, 35, 35;
--darkdocument-background-color: 8, 12, 14;

--side-bar-link-color-hover: var(--basalt-light-text-color);
--top-bar-dropdown-link-color-hover: var(--basalt-light-text-color);
--login-status-text-color-hover: var(--basalt-light-text-color);
--selection-text-color: var(--basalt-light-text-color);

--two-color: var(--admo-color-bright) !important;

}

[[/module]]
