[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Courgette&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,800;1,800&display=swap');

:root {
  --head-font: 'Monserrat', sans-serif;

  --link-txt-color: rgb(var(--accent)) !important;
  --one-color: var(--accent);
}

.fancyhr,
.fancyborder {
  filter: grayscale(1);
}

#page-content .table0 {
  --accent: 175, 175, 175;
  --link-txt-color: rgb(175, 175, 175);
}

#page-content .bt {
  color: rgb(var(--accent));
}

@keyframes rotation {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(359deg);
  }
}

#page-content tr th {
  text-align: center !important;
}

:is(.fancyborder) {
  border-radius: 4vb;
}

.fancyborder .fnnum::after {
  border-bottom: 1px solid var(--dark-bg-1);
}

.cards {
  display: flex;
  flex-wrap: nowrap;
  justify-content: center;
}

.cards > div {
  margin: -12px;
}

.cards a {
  border-bottom: none !important;
}

.cards a:hover,
.cards a::selection {
  background-color: rgb(var(--accent));
}

.cards.main > :is(:nth-child(3), :nth-child(4)) {
  --accent: var(--one-color);
  filter: opacity(.33);
  cursor: not-allowed;
  color: transparent !important;
  background-color: transparent !important;
}

.cards.tabs > div:nth-child(2) {
  --accent: var(--one-color);
  filter: opacity(.33);
  cursor: not-allowed;
}

@media (max-width: 767px) {
  .cards.tabs > div:nth-child(2) {
    display: none;
  }
}

.cards.tabs > div:nth-child(1) .yui-navset .yui-nav li {
  width: 50%;
}

.cards.main > :nth-child(1),
.cards.tabs > div:nth-child(1) :is(.yui-navset .yui-nav li:nth-of-type(1), .yui-content > :nth-of-type(1) *) {
  --accent: 0, 150, 150 !important;
}
.cards.main > :nth-child(2),
.cards.tabs > div:nth-child(1) :is(.yui-navset .yui-nav li:nth-of-type(2), .yui-content > :nth-of-type(2) *) {
  --accent: 150, 0, 0 !important;
}

.yui-navset .yui-nav {
  border: none !important;
}

.yui-navset .yui-nav li {
  min-width: 25%;
}

.yui-navset .yui-content {
  background: none !important;
  margin-top: -1px;
  padding: 0;
}

.yui-navset .yui-content > div {
  background: none !important;
  padding: 0;
  margin: 0;
}

.yui-navset .yui-content .modal {
  width: auto;
  margin: auto;
}
