[[module CSS]]
#page-title {
    display: none;
}

#header {
  background-image: url('https://scp-wiki.wdfiles.com/local--files/fragment%3Ascp-6500-14/CorbenicHeader8.svg')
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "监督者议会";
    color: #eee;
    font-family: 'Liu ti';

}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "维持，保存，延长";
    font-weight: bold;
    color: #F48337;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
    font-family: 'Liu ti';
}
div#container-wrap {
    background: url('https://scp-wiki.wdfiles.com/local--files/fragment%3Ascp-6500-14/corbenic_bg.png') repeat-x;
}
body {
    background-color: #FFEADB;
}
#page-content {
    font-family: 'Bree Serif', 'kaiti';
    font-size: 12.5pt;
}
#main-content a
{
color:#1E191A;
}
/* ---- WORDS NO LONGER BROKEN, THE CROQUEMBOUCHE HAS SPOKEN ---- */
span, a { word-break: normal !important }
}
div.info-container > div.collapsible-block {
    padding: 0 !important;
    margin: 0 !important;
    box-shadow: none !important;
}
.type1 {
    background-attachment: scroll;
    background: linear-gradient(rgba(220, 240, 240, 0.3), rgba(240, 240, 220, 0.5)), url(http://scp-wiki.wdfiles.com/local--files/scp-5862/paperbackground.jpg);
    background-clip: border-box;
    background-origin: padding-box;
    background-repeat: repeat;
    background-size: 100%;
    padding: 25px;
}
.type1 p,
.type1 ul {
    line-height: 25px;
    margin: none;
}

.page-rate-widget-box {
     margin: unset;
     border-radius: 0;
     border: solid 2px #21252E;
     background-color: #21252E;
     margin-top: 4px;
     margin-bottom:4px;
     margin-right: 3px;
}
.page-rate-widget-box .rate-points {
    background-color: #21252E !important;
    border: none;
    color: #fff !important;
    text-transform: capitalize;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #21252E;
    color: #fff !important;
    border-top: none;
    border-bottom: none;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #fff !important;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #fff !important;
    color: #21252E !important;
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: #21252E;
    border: none;
}
.page-rate-widget-box .cancel a {
    color: #fff !important;
}
.page-rate-widget-box .cancel a:hover {
    background: #fff !important;
    color: #21252E !important;
}
.footnotes-footer {
display:none;
}
[[/module]]
