[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Courgette&display=swap');

:root {
  --header-title: 'OVERWATCH';
  --header-subtitle: 'SACRIFICE CONFERS POWER';

  --accent: var(--five-color);
  --link-txt-color: rgb(var(--accent));
}

#page-content tr th {
  text-align: center !important;
}

.notation .jotting {
  min-width: 10em;
  width: 75%;
  margin: auto;
}

#page-content .bt {
  color: rgb(var(--accent));
}

:is(.fancyborder) {
  border-radius: 4vb;
}

.fancyborder .fnnum::after {
  border-bottom: 1px solid var(--dark-bg-1);
}

.page {
    color: #000;
    font-size: 140%;
    font-family: "Monotype Corsiva", "Bradley Hand ITC", sans-serif;
    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;
    padding: 25px;
}
.page p,
.page ul {
    line-height: 20px;
    margin: none;
}

@import url('https://fonts.googleapis.com/css?family=Bahianita|Cabin+Sketch|Miss+Fajardose|Nothing+You+Could+Do|Open+Sans|Reenie+Beanie|Ruthie|Tangerine&display=swap');

div.info-container > div.collapsible-block {
    padding: 0 !important;
    margin: 0 !important;
    box-shadow: none !important;
}

.type1 {
    font-family: "Monotype Corsiva", "Bradley Hand ITC", sans-serif;
    font-size: 140%;
    color: #000;
    background-attachment: scroll;
    background: linear-gradient(rgba(220, 240, 240, 0.3), rgba(240, 240, 220, 0.5)), url(https://scp-wiki.wdfiles.com/local--files/scp-5862/paperbackground.jpg);
    background-clip: border-box;
    background-origin: padding-box;
    background-repeat: repeat;
    background-size: 100%;
    padding: 25px;
}
.type1 p,
.type1 ul {
    line-height: 25px;
    margin: none;
}

.wood {
    background-image: linear-gradient(transparent, #111 400px, #111 400px, #111 100%), url(https://scp-wiki.wdfiles.com/local--files/theme%3Alaruetheme/tilewood.jpg);
    color: #eee;
   font-weight: bold;
   padding: 20px;
}

@import url('https://fonts.googleapis.com/css2?family=Anton&family=Playfair+Display+SC&display=swap');

div.newspaper-border {
    padding: 1.5em;
    margin: 2em;
    background: #FFF;
    box-shadow: 0px 4px 13px rgba(0, 0, 0, 0.8);
    color: var(--dark-bg-1);
}
div.newspaper-border hr {
    background: var(--dark-bg-1) !important;
}

div.newspaper-main {
    font-family: 'Special Elite', sans-serif;
}

div.newspaper-title {
    font-family: 'Playfair Display SC', serif;
    font-size: 4em;
    text-align: center;
    margin: 0.25em;
}

div.newspaper-banner {
    font-family: serif;
    font-size: 110%;
    border-top-style: solid;
    border-bottom-style: solid;
}

div.newspaper-headline {
    font-family: 'Anton', serif;
    margin: 2px 0 5px;
    text-align: center;
}

div.newspaper-reporter {
    font-family: serif;
    font-size: 120%;
    font-style: italic;
    text-align: center;
}

div.newspaper-body {
    font-family: serif;
    font-size: 1.2em;
    text-align: justify;
    text-justify: inter-word;
    line-height: 1.4em;
    padding: 1em;
}

div.newspaper-body div.scp-image-block {
    margin: 0.5em 0 0 1em;
}

div.rejection {
    border: 4px solid #ff4040;
    border-radius: 30px;
    font-weight: bold;
    color: #ff4040;
    text-align: center;
    margin: 0.5em;
}

div.rejection-wrap {
     transform: rotate(-3.8deg);
     width: 35vw;
     margin-bottom: 1em;
     margin-left: 3em;
}

div.appeal {
    margin-top: 5em;
}
