[[include :scp-wiki:theme:admo]]

[[module CSS]]

:root {

--admo-backdrop: url(http://scp-jp-storage.wikidot.com/local--files/file:6370408-55-zl3g/admo-6659-backdrop.webp);

--admo-color-bright: 194, 85, 0;
--admo-color-dim: 24, 7, 0;
--admo-color-dark: 7, 4, 0;
--admo-color-alt: 161, 75, 8;

--general-border-color: 77, 44, 31;
--basalt-tertiary-color: 35, 21, 8;
--darkdocument-background-color: 19, 7, 2;

--side-bar-link-color-hover: var(--basalt-light-text-color);
--top-bar-dropdown-link-color-hover: var(--basalt-light-text-color);
--login-status-text-color-hover: var(--basalt-light-text-color);
--selection-text-color: var(--basalt-light-text-color);

--four-color: var(--admo-color-bright) !important;

}

[[/module]]

[[div class="admo-accessibility"]]
