[[module CSS]]

:root {

--admo-backdrop: url(http://scptestwiki.wikidot.com/local--files/sigma:admonition-redesign-6747/admo-6747-backdrop.webp);

--admo-color-bright: 130, 80, 160;
--admo-color-dim: 11, 5, 26;
--admo-color-dark: 5, 0, 8;
--admo-color-alt: 116, 77, 135;

--general-border-color: 37, 30, 50;
--basalt-tertiary-color: 34, 10, 44;
--darkdocument-background-color: 14, 4, 18;

--side-bar-link-color-hover: var(--basalt-light-text-color);
--top-bar-dropdown-link-color-hover: var(--basalt-light-text-color);
--login-status-text-color-hover: var(--basalt-light-text-color);
--selection-text-color: var(--basalt-light-text-color);

--one-color: 34, 187, 68 !important;
--six-color: var(--admo-color-bright);

}

[[/module]]
