[[module css]]
span, a { word-break: normal !important }
[[/module]]

[[module css]]
div#container-wrap {
    background: url('http://scp-jp-storage.wdfiles.com/local--files/file%3A2375337-600-qu3r/bg.png') top left repeat-x;
    background-size: 105px;
}
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* for menu */
    background: url('http://scp-jp-storage.wdfiles.com/local--files/file%3A2375337-599-87ev/OSATlogosmall.png') 10px 35px no-repeat;
}
#header h2 span::before {
    content: "Áúü„ÅÆÊÑõÂõΩÂøÉ";
    font-family: "Stardos Stencil", "Times New Roman", serif;
    font-weight: 700;
    font-weight: bold;
    font-style: normal;
    color: #FFFF92;
    text-shadow: 0 1px 3px rgba(0,0,0,.8)
}
#header h1 a::before {
    content: "GC.CA/OSAT";
    font-family: "Stardos Stencil", "Times New Roman", serif;
    font-weight: 700;
    color: FFFF92;
    font-style: normal;
    text-shadow: 0 1px 3px rgba(0,0,0,.8)
}
.scp-image-block .scp-image-caption {
   font-family: 'Verdana', sans-serif;
   font-size: 9pt;
   background-color:  #ffffff;
   color: black;
}
h1, h2, h3, h4, h6, h7 {
    font-family: 'Oswald', sans-serif;
    font-style: normal;
    color: black;
    font-weight: bold;
}

#page-title {
    border-color: black;
}

.blockquote, blockquote {
    background-color: #f4f4f4;
    color: black;
    font-family: 'Verdana', sans-serif;
    border: 4px solid #d84519;
[[/module]]

[[module css]]
div.page-rate-widget-box .rate-points {
    background-color: #633;
    border: solid 1px #633;
    border-right: 0;
    border-radius: 5px 0 0 5px;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #fff6f0;
    border-top: solid 1px #633;
    border-bottom: solid 1px #633;
    font-weight: bold;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #633;
    padding: 0 4px;
    margin: 0 1px;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #633;
    color: #fffff0;
    text-decoration: none;
}

.page-rate-widget-box .cancel {
    background-color: #633;
    border: solid 1px #633;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}

.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #966;
}

.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #633;
    color: #fffff0;
    text-decoration: none;
}
a {
    color: #9c3111;
    text-decoration: bold;
}

a.newpage {
    color: #FF0000;
    text-decoration: bold;
}

#side-bar a:visited,
#interwiki a:visited {
    color: #9c3111;
}

a:visited {
    color: #E10000;
}
div.console{
    background-color: black;
    border: double 3px #80FF80;
    margin-top: 18px;
    margin-bottom: 12px;
    padding-left: 18px;
    padding-right: 18px;
    color: #80FF80;
    font-family: monospace;
    font-size: 12pt;
}
div.console::before{content:"43NET COMMAND PROMPT";
border: double 3px #80FF80;
margin: auto;
width: 50%;
color: #80FF80;
background-color: black;
padding: 1px 3px;
font-weight: bold;
letter-spacing: 2px}
}
span, a { word-break: normal !important }
}
[[/module]]

