/* Clear floats after the columns */
.row:after {
  content: "";
  display: table;
  clear: both;
}
* {
  box-sizing: border-box;
}

/* Set a background color */
.timeline-flex {
  position: relative;
  max-width: 1200px;
  margin: 15px auto;
margin-top:5px;
  background-color: rgb(187,225,250); /* background blue*/
  border: 5px solid rgb(187,225,250);
border-radius:10px;
overflow:hidden;
  display: flex;
  flex-wrap: wrap;
}

/* The actual timeline (the vertical ruler) */
.timeline-flex::after {
  content: '';
  position: absolute;
  width: 6px;
  background-color: black;
  top: 0;
  bottom: 0;
  left: 50%;
  margin-left: -3px;
}

/* Container around content */
.container {
  padding: 10px 30px;
  position: relative;
  background-color: inherit;
  border-radius: 10px;
  flex-basis: 50%;
color: white;
}

/* The circles on the timeline */
.left::after,
.right::after
 {
    content: '';
    position: absolute;
    width: 25px;
    height: 25px;
    right: -17px;
    background-color: #E09F1F;
    border: 4px solid black;
    top: 15px;
    border-radius: 50%;
    z-index: 1;
overflow:visible;
}

.right::after {
    left: -16px;
}



/* Bubble around the text. */
.time-content {
  padding: 10px 15px;
  background-color: #424248;
  position: relative;
  border-radius: 6px;
}

.timeline-image > img {
    width: 20em;
    border-radius: 6px;
    border: 4px solid #424248;
background-color: rgb(244,244,244)
}

.timeline-image {
   text-align: center;
  border-radius:6px;
}


/* Media queries - Responsive timeline on screens less than 600px wide */
@media screen and (max-width: 600px) {
/* Place the timelime to the left */
  .timeline::after {
    left: 0px;
flex-basis:0;
  }

/* Full-width containers */
  .container {
    width: 100%;
    padding-left: 70px;
    padding-right: 25px;
color:white;
  }



/* Make sure all circles are at the same spot */
  .left::after, .right::after {
    left: -17px;
  }

/* Make all right containers behave like the left ones */
  .right {
    left: 0%;
  }
  .timeline-flex {
 display: block;
        border-left: 6px solid black;
border-top-left-radius:0;
border-bottom-left-radius:0;
    }

    .timeline-flex::after {
        content: unset;
left:31px;
    }



