[[module css]]

:root {
    --basalt-primary-color: 50,50,50;
    --basalt-secondary-color: 55,55,55;
    --basalt-tertiary-color: 218,219,222;
    --basalt-main-text-color: 254,254,254;
    --basalt-overtone: 50,50,50;
    --basalt-undertone: 155,154,162;

    --basalt-bright-element-color: 16,76,184;
    --basalt-dark-element-color: 18,20,150;
    --basalt-alternate-color: 255,132,0;
    --basalt-positive-color: 24,163,20;
    --basalt-negative-color: 255,48,48;
    --basalt-cancel-color: var(--basalt-negative-color);
    --basalt-sub-text-color: 155,154,162;
    --basalt-light-text-color: var(--basalt-primary-color);

    --basalt-UI-dark-palette: var(--basalt-overtone);
    --basalt-focus-color: var(--basalt-undertone);
    --basalt-background-color: var(--basalt-primary-color);
}
#page-content .wiki-content-table tr th {
    border: solid 1px #000 !important;
box-shadow:0px 0px 0px #000000;
}
.notation {
    border-left: solid 3px rgb(30, 30, 30);
    border-right: solid 3px rgb(30, 30, 30);
    padding: 25px 25px 25px 25px;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.3);
    width: 75%;
    margin: auto;
    background: --basalt-secondary-color
}
.email-example .collapsible-block-folded a.collapsible-block-link {
    animation: blink 0.8s ease-in-out infinite alternate;
border:none;
}
@keyframes blink {
    0% { color: transparent; }
    50%, 100% { color: rgb(255,48,48); }
}

.newlink {
    animation: flash 0.8s ease-in-out infinite alternate;
border:none;
}
@keyframes flash{
    0% { color: transparent; }
    50%, 100% { color: orange; }
}

#page-content .wiki-content-table tr td {
    border: 1px #000 solid !important;
box-shadow:0px 0px 0px #000000;
}
table.wiki-content-table{
margin:0;
border: 0px solid black;
}
table.wiki-content-table tr td {
    border: 0px solid black;
}
table.wiki-content-table tr th {
    border:0px solid black;
}
.terminal {
    padding: 15px 15px 15px 15px;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.3);
    width: auto;
    margin: auto;
    background: black;
    border: solid 2px #5D5D5D;
    color:orange;
    font-family:"Lucida Console", monospace;
    text-align: left;
}
.terminal a:link, a:visited {
text-decoration: underline, bold;
color: orange;
}
[[/module]]
