[[module CSS]]

:root {
--bgColor: rgb(255, 255, 255);
--fgColor: rgb(240, 240, 240);
--textColor: hsl(37, 100%, 5%);
--textColorAlt: var(--textColor);
--accentColor: #807100;
}

 #search-top-box-form input[type="submit"]{
     color: var(--accentColor);
     border-color: var(--accentColor);
}
 #search-top-box-form input[type="submit"]:hover{
     color: var(--keyColor);
     background: var(--accentColor) !important;
}

/* FILTER HOTFIX */
 #page-content h2,
 #page-content h3,
 #page-content h4,
 #toc .title,
 strong,
 .bt,
 .listPagesNav-prev p, .listPagesNav-next p,
 .related > p:nth-child(1)::before,
 .related > ol:nth-child(1)::before,
 .related > ul:nth-child(1)::before,
 .related > a:nth-child(1)::before,
 #page-content .earthworm a{
     filter: saturate(.85) brightness(1.15);
}

body {
background-image: none;
}

@media (prefers-reduced-motion: no-preference) {
  #container-wrap-wrap:before,
  #container-wrap-wrap:after {
box-shadow: none;
}

  #container-wrap:before,
  #container-wrap:after {
box-shadow: none;
}

  #container:before,
  #container:after {
box-shadow: none;
}

#header h1 a::before, #header h2::before, h1, h2, h3, h4, h5, h6 {
filter: none;
color: var(--accentColor);
background: none;
}

#top-bar .open-menu a, #main-content h1, #main-content h2, #main-content h3, #main-content h4, #main-content h5, #main-content h6 {
filter: none;
}

#extra-div-1 {
  filter: brightness(60%) opacity(0.25);
}

[[/module]]

