    text-align: left;
    background:#BDBDBD;
    width:100%;
    padding:1px 15px;
    margin: 10px auto;
}

#page-title{display:none}

span.oraclet{
   font-family: 'Audiowide', regular;
   font-size: 400%;
   color: navy;
}

span.oraclesh{
   font-family: 'Audiowide', regular;
     font-size: 125%;
}

div.oracle{
    background: #BDBDBD;
    color:black;
    border: none;
    border-top: 5px solid navy;
    border-bottom: 5px solid navy;
    padding: 0 1em;
    margin: 0em 0em;
width: 96%;
