[[module CSS]]

@import url('https://scp-wiki.wikidot.com/local--files/fragment:scp-7503-3/apex-lake.css');
@import url('https://scp-wiki.wikidot.com/local--files/fragment:scp-7503-3/rusch.css');
@import url('https://fonts.googleapis.com/css2?family=Zhi+Mang+Xing&display=swap');

:lang(cn) {

    --header-title: "宗座贞洁成圣会";

/* Typefaces */
        --decorative-font: 'Zhi Mang Xing', sans-serif;

/* Standard Colors */
        --white-monochrome: 220, 220, 220;
        /* white */

/* Rating Module Colors */
        --rating-module-button-color: var(--gray-monochrome);
        --rating-module-button-plus-color: 45, 65, 25;
        --rating-module-button-negative-color: 70, 35, 30;
        --rating-module-button-cancel-color: 50, 45, 50;
        --rating-module-button-credit-color: 50, 45, 50;
        --rating-module-text-color: var(--gray-monochrome);
        --rating-module-text-hover-color: var(--swatch-menutxt-light-color)
}

body {
  font-size: 102%;
}

a:visited {
  color: #b99650;
  background-color: transparent;
  text-decoration: none;
}

.collapsible-block-unfolded-link {
    color: #b99650;
}

.collapsible-block-link {
    color: #b99650;
     font-weight: bold;
     font-size: 125%;;
}

.collapsible-block-link {
    color: #b99650;
     font-weight: bold;
     font-size: 125%;;
}

#header h2 span {
        margin-left: calc(var(--header-height-on-desktop) + 0.75rem);
        padding-top: 20px;
    }

#header h2 span::before {
    content: '净化，守护，宗座';
    color: #F1F1F1;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
    padding-inline-start: 30px;
}

[[/module]]

[[module CSS]]
.fnnum::after, .fncon::before {
    content: counter(megacount, upper-roman);
}
.fnnum::after {
    color: rgb(var(--bright-accent));
}
.fncon::before, .fnnum:hover {
    background-color: rgb(var(--medium-accent));
}
.fncon {
    padding-top: 1rem;
    background: rgb(var(--dark-gray-monochrome));
    border: none;
}
[[/module]]

