[[module CSS]]
:root:lang(cn) {
--logo: url(https://scp-wiki.wikidot.com/local--files/scp-7616/Sigil_Main.png);
--title: "SCP基金会";
--subtitle: "有关月见草·法蒂什·冯·特雷维尔之事";
}

.document {
    background: white url('https://scp-wiki.wikidot.com/local--files/scp-7616/SCP_Trans.png') center center no-repeat;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
    margin: 26px auto 0;
    max-width: 550px;
    min-height: 300px;
    padding: 24px;
    padding-top: 50px;
    position: relative;
    width: 80%;
    color: black;
}

.document:before,
.document:after {
    content: " ";
    height: 98%;
    position: absolute;
    width: 100%;
    z-index: -1;
}

.document:before {
    background: #fafafa;
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.2);
    left: -5px;
    top: 4px;
    transform: rotate(-2.5deg);
}

.document:after {
    background: #f6f6f6;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.2);
    right: -3px;
    top: 1px;
    transform: rotate(1.4deg);
}


