body {

    background-image: url(https://scp-wiki.wdfiles.com/local--files/whore-of-blood-hub/HellishSCPBackground.png);
    background-attachment: fixed;
    background-size: cover;
}
:root{
--logo: url(https://scp-wiki.wdfiles.com/local--files/whore-of-blood-hub/Primrose-Sigil-white-badge.png);
--title: "SCP Foundation";
--subtitle: "WHORE OF BLOOD";
}

.document {
    background: white url('https://scp-wiki.wikidot.com/local--files/scp-7616/SCP_Trans.png') center center no-repeat;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
    margin: 26px auto 0;
    max-width: 550px;
    min-height: 300px;
    padding: 24px;
    padding-top: 50px;
    position: relative;
    width: 80%;
    color: black;
}

.document:before,
.document:after {
    content: " ";
    height: 98%;
    position: absolute;
    width: 100%;
    z-index: -1;
}

.document:before {
    background: #fafafa;
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.2);
    left: -5px;
    top: 4px;
    transform: rotate(-2.5deg);
}

.document:after {
    background: #f6f6f6;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.2);
    right: -3px;
    top: 1px;
    transform: rotate(1.4deg);
}


