[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Big+Shoulders+Display:wght@800&display=swap');

:root {
    --timeScale: 1.5;
    --red: 176, 216, 247;
     --medium-accent: var(--red);
     --cutColour: #fafafa;
     --barColour: var(--red);
     --toggle-border-color: rgb(var(--red));
     --toggle-icon-color: rgb(var(--red));
     --mono-font: 'IBM Plex Mono', monospace;
     --background-gradient-distance: 20rem;
     --background-gradient-color: var(--red);
     --dark-accent: var(--red);
     --barColour: var(--red);
     --bright-accent: var(--red);

     --link-color: 140, 199, 245;
     --visited-link-color: 176, 216, 247;
     --hover-link-color: 176, 216, 247;

    --logo-image: url("https://nu-scptheme.github.io/Black-Highlighter/img/logo.svg");
}

#content-wrap {
    background-color: #ffffff;
    background-image: url("data:image/svg+xml,%3Csvg width='48' height='64' viewBox='0 0 48 64' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M48 28v-4L36 12 24 24 12 12 0 24v4l4 4-4 4v4l12 12 12-12 12 12 12-12v-4l-4-4 4-4zM8 32l-6-6 10-10 10 10-6 6 6 6-10 10L2 38l6-6zm12 0l4-4 4 4-4 4-4-4zm12 0l-6-6 10-10 10 10-6 6 6 6-10 10-10-10 6-6zM0 16L10 6 4 0h4l4 4 4-4h4l-6 6 10 10L34 6l-6-6h4l4 4 4-4h4l-6 6 10 10v4L36 8 24 20 12 8 0 20v-4zm0 32l10 10-6 6h4l4-4 4 4h4l-6-6 10-10 10 10-6 6h4l4-4 4 4h4l-6-6 10-10v-4L36 56 24 44 12 56 0 44v4z' fill='%23eff7fd' fill-opacity='0.4' fill-rule='evenodd'/%3E%3C/svg%3E");
}

#page-content hr {
border-top: none;
height: .05em;
background: #454545;
}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;
}

#page-content > .collapsible-block {
    display: block;
    text-align: center;
    padding: .8em;
    background-color: #e3e3e3;
    border-top: 0.125rem dashed #b5b5b5;
    border-bottom: 0.125rem dashed #b5b5b5;
}

#page-content > .collapsible-block .collapsible-block-content {
    text-align: left;
}

blockquote,
div.blockquote {
    border: 0.125rem solid #d4d4d4;
    background-color: #F5F5F5;
}

.interview{
    border:solid 2px #d4d4d4;
    background:#F5F5F5;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}

.addendum{
    border:solid 1px #5e5e5e;
    background:#ffffff;
    width:90%;
    padding:10px 15px;
    margin: 10px auto;
}

.paper{
    border-top:solid 2px #b0d8f7;
    border-bottom:solid 2px #b0d8f7;
    background:#fafafa;
    width:60%;
    padding:1px 15px;
    margin: 10px auto;
}

.title{
    font-family: 'Big Shoulders Display', cursive;
    text-align: center;
    font-size: 110%;
}

.ooo{
    border-right:solid 2px #6bb6f0;
    border-left:solid 2px #6bb6f0;
    background:#F5F5F5;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}

.testfile{
    border:solid 3px #6bb6f0;
    background: #f5fafe;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}

.testvideo{
    background:#deeffc;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}

.info-container{
--barColour: #b0d8f7;
}

#page-title{display:none}

[[/module]]



