[[include :scp-wiki:component:toggle-sidebar]]

[[module CSS]]
#page-title {
    display: none;
}
.fakeprot .mailform-box .buttons{
  display:none;
}
.fakeprot + .collapsible-block .collapsible-block-link {
  padding: 0.1em 0.5em;
  text-decoration: none;
  background-color: #F4F4F4;
  border: 1px solid #AAA;color: #000;
}
.fakeprot + .collapsible-block .collapsible-block-link:hover {
  background-color: #DDD;
  color: #000;
}
.fakeprot + .collapsible-block .collapsible-block-link:active {
  background-color: #DDD;
  color: #000;
}
.fakeprot + .collapsible-block .collapsible-block-unfolded-link{
  margin:0.5em 0;
  text-align: center;
}
.fakeprot + .collapsible-block .collapsible-block-folded{
  margin:0.5em 0;
  text-align: center;
}
.fakeprot .passw input[type=text] {
  text-security:disc;
  -webkit-text-security:disc;
  -mox-text-security:disc;
}
.mailform-box td:first-child {
  width: 80px;
}
.buttonwrapper {
  text-align: center;
}
.loginbtn {
  background-color: Maroon;
  text-decoration: none; 
  color: #FFF;
  padding: 10px 100px;
  border: none;
  cursor: pointer;
  opacity: 0.9;
  transition: 0.09s;
  border-radius: 3px;
  transition 0.8s;
}
.loginbtn:visited {
  background-color: Maroon;
  text-decoration: none; 
  color: #FFF;
}
.loginbtn:focus {
  background-color: Maroon;
  text-decoration: none; 
  color: #FFF;
  outline: 0;
}
.loginbtn:hover {
  background-color: Maroon;
  text-decoration: none; 
  color: #FFF;
  transition: 0.8s;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.7);
}
.loginbtn:active {
  background-color: Maroon;
  text-decoration: none; 
  color: #FFF;
  transition: 0.09s;
  transform: translateY(1px);
  box-shadow: 0 0px 0px 0 rgba(0, 0, 0, 0);
}
.forgotbtn:visited,
.forgotbtn:link, 
.forgotbtn:hover,
.forgotbtn:active {
  background-color: white;
  text-decoration: none; 
  color: #000;
  border: none;
  cursor: pointer;
  font-weight: bold;
}
.scipnet {
  font-size:55px; 
  font-weight: bold;
  text-align: center;
  margin-bottom: -1em;
  margin-top: 0em;
}
[[/module]]

